        [3083] = {5007,"Quan Vũ","Đây là thủ cấp Văn Xú, hãy tiếp lấy! (Lập tức hiến thủ cấp của Văn Xú cho Tào Tháo.)","Đây là thủ cấp Văn Xú, hãy tiếp lấy! (Lập tức hiến thủ cấp của Văn Xú cho Tào Tháo.)","","",},
        [3084] = {5007,"Tào Tháo","Trảm liên tiếp hai đại tướng của Viên Thiệu, Vân Trường với Dương Uy Tướng quả thật là anh hùng đương thời.","Trảm liên tiếp hai đại tướng của Viên Thiệu, Vân Trường với Dương Uy Tướng quả thật là anh hùng đương thời.","Quan Vũ","",},
        [3085] = {5008,"Viên Thiệu","Lần này lại là Quan Vũ tiêu diệt Văn Xú, sao ngươi dám làm như vậy!","Lần này lại là Quan Vũ tiêu diệt Văn Xú, sao ngươi dám làm như vậy!","Lưu Bị","",},
        [3086] = {5008,"Lưu Bị","Ta có tội gì?","Ta có tội gì?","Viên Thiệu","",},
        [3087] = {5008,"Viên Thiệu","Ngươi sai nghĩa đệ trảm tiếp một đại tướng của ta, sao không có tội?","Ngươi sai nghĩa đệ trảm tiếp một đại tướng của ta, sao không có tội?","Lưu Bị","",},
        [3088] = {5008,"Lưu Bị","Hãy nghe ta giải thích: Tào Tháo biết Bị ở chỗ minh công, sợ Bị trợ giúp minh công nên sai Vân Trường tru sát hai tướng.","Hãy nghe ta giải thích: Tào Tháo biết Bị ở chỗ minh công, sợ Bị trợ giúp minh công nên sai Vân Trường tru sát hai tướng.","Viên Thiệu","",},
        [3089] = {5008,"Lưu Bị","Minh công biết chắc chắn sẽ giận dữ, Tháo muốn mượn tay minh công diệt trừ ta. Mong minh công nghĩ lại.","Minh công biết chắc chắn sẽ giận dữ, Tháo muốn mượn tay minh công diệt trừ ta. Mong minh công nghĩ lại.","Viên Thiệu","",},
        [3090] = {5008,"Viên Thiệu","Huyền Đức nói đúng lắm! Thiếu chút là các ngươi làm ta hại người hiền rồi.","Huyền Đức nói đúng lắm! Thiếu chút là các ngươi làm ta hại người hiền rồi.","Lưu Bị","",},
        [3091] = {5008,"Lưu Bị","Minh công khoan hồng, Bị không biết lấy gì báo đáp. Nay muốn sai một người tâm phúc đem mật thư đưa cho Vân Trường","Minh công khoan hồng, Bị không biết lấy gì báo đáp. Nay muốn sai một người tâm phúc đem mật thư đưa cho Vân Trường","Viên Thiệu","",},
        [3092] = {5008,"Lưu Bị","Vân Trường biết tin ta ở đây sẽ lập tức đến giúp minh công cùng đánh Tào Tháo, báo thù cho Nhan Lương, Văn Sú, minh công nghĩ thế nào?","Vân Trường biết tin ta ở đây sẽ lập tức đến giúp minh công cùng đánh Tào Tháo, báo thù cho Nhan Lương, Văn Sú, minh công nghĩ thế nào?","Viên Thiệu","",},
        [3093] = {5008,"Viên Thiệu","Ta được Vân Trường tức là được một viên tướng giỏi gấp mười lần Nhan Lương, Văn Sú.","Ta được Vân Trường tức là được một viên tướng giỏi gấp mười lần Nhan Lương, Văn Sú.","Lưu Bị","",},
        [3094] = {5008,"Lưu Bị","Ta lập tức đi viết thư và sai ngươi đưa đi.","Ta lập tức đi viết thư và sai ngươi đưa đi.","Viên Thiệu","",},
        [3095] = {5101,"Trương Liêu","Nghe nói huynh nghe được tin Huyền Đức nên đến chúc mừng.","Nghe nói huynh nghe được tin Huyền Đức nên đến chúc mừng.","Quan Vũ","",},
        [3096] = {5101,"Quan Vũ","Chúa công tuy ở đó nhưng chưa được gặp, có gì đáng mừng!","Chúa công tuy ở đó nhưng chưa được gặp, có gì đáng mừng!","Trương Liêu","",},
        [3097] = {5101,"Trương Liêu","Huynh với Huyền Đức, so giữa đệ với huynh, bên nào thân hơn?","Huynh với Huyền Đức, so giữa đệ với huynh, bên nào thân hơn?","Quan Vũ","",},
        [3098] = {5101,"Quan Vũ","Ta với huynh chỉ là bằng hữu. Ta với Huyền Đức là bằng hữu và huynh đệ, cũng là chủ thần, sao so sánh được?","Ta với huynh chỉ là bằng hữu. Ta với Huyền Đức là bằng hữu và huynh đệ, cũng là chủ thần, sao so sánh được?","Trương Liêu","",},
        [3099] = {5101,"Trương Liêu","Nay Huyền Đức ở Hàn Bắc, huynh có đi đến đó không?","Nay Huyền Đức ở Hàn Bắc, huynh có đi đến đó không?","Quan Vũ","",},
        [3100] = {5101,"Quan Vũ","Lời nói ngày trước, sao làm trái được! Nhờ Văn Viễn nói hộ cho ta với thừa tướng.","Lời nói ngày trước, sao làm trái được! Nhờ Văn Viễn nói hộ cho ta với thừa tướng.","Trương Liêu","",},
        [3101] = {5102,"Tào Tháo","Vân Trường đi có vội quá không?","Vân Trường đi có vội quá không?","Quan Vũ","",},
        [3102] = {5102,"Quan Vũ","Quan mỗ từng thưa với thừa tướng trước, nay chủ cũ ở Hà Bắc, tất nhiên phải vội đi.","Quan mỗ từng thưa với thừa tướng trước, nay chủ cũ ở Hà Bắc, tất nhiên phải vội đi.","Tào Tháo","",},
        [3103] = {5102,"Quan Vũ","Đã mấy lần đến phủ nhưng không được gặp, cho nên đã viết bức thư cáo từ, gói vàng bạc, ấn nộp lại thừa tướng. Xin thừa tướng đừng quên lời hứa ngày trước.","Đã mấy lần đến phủ nhưng không được gặp, cho nên đã viết bức thư cáo từ, gói vàng bạc, ấn nộp lại thừa tướng. Xin thừa tướng đừng quên lời hứa ngày trước.","Tào Tháo","",},
        [3104] = {5102,"Tào Tháo","Ta sao có thể làm trái lời hứa, ta chỉ sợ tướng quân đi đường thiếu thốn, nên đem ít đồ hành trang đến đưa tiễn. (Nói xong bưng mâm vàng đến.)","Ta sao có thể làm trái lời hứa, ta chỉ sợ tướng quân đi đường thiếu thốn, nên đem ít đồ hành trang đến đưa tiễn. (Nói xong bưng mâm vàng đến.)","Quan Vũ","",},
        [3105] = {5102,"Quan Vũ","Ta nhiều lần được thừa tướng ban thưởng, vẫn còn đủ dùng. Xin để lại số vàng này thưởng cho tướng sĩ.","Ta nhiều lần được thừa tướng ban thưởng, vẫn còn đủ dùng. Xin để lại số vàng này thưởng cho tướng sĩ.","Tào Tháo","",},
        [3106] = {5102,"Tào Tháo","Món quà nhỏ thưởng công lớn, sao phải từ chối chứ?","Món quà nhỏ thưởng công lớn, sao phải từ chối chứ?","Quan Vũ","",},
        [3107] = {5102,"Quan Vũ","Chút công lao nhỏ có đáng là gì.","Chút công lao nhỏ có đáng là gì.","Tào Tháo","",},
        [3108] = {5102,"Tào Tháo","Vân Trường là nghĩa sĩ, tiếc là ta kém phúc không lưu lại được. Chiếc cẩm bào xem như chút lòng thành.","Vân Trường là nghĩa sĩ, tiếc là ta kém phúc không lưu lại được. Chiếc cẩm bào xem như chút lòng thành.","Quan Vũ","",},
        [3109] = {5102,"Quan Vũ","Đội ơn thừa tướng ban áo, sau này sẽ gặp lại.","Đội ơn thừa tướng ban áo, sau này sẽ gặp lại.","Tào Tháo","",},
        [3110] = {5103,"Khổng Tú","Tướng quân đi đâu?","Tướng quân đi đâu?","Quan Vũ","",},
        [3111] = {5103,"Quan Vũ","Ta vừa cáo từ thừa tướng, sang Hàn Bắc tìm huynh.","Ta vừa cáo từ thừa tướng, sang Hàn Bắc tìm huynh.","Khổng Tú","",},
        [3112] = {5103,"Khổng Tú","Hàn Bắc Viên Thiệu đối đầu với thừa tướng. Tướng quân qua đó chắc là có công văn của thừa tướng?","Hàn Bắc Viên Thiệu đối đầu với thừa tướng. Tướng quân qua đó chắc là có công văn của thừa tướng?","Quan Vũ","",},
        [3113] = {5103,"Quan Vũ","Vì đi đường vội vã nên chưa kịp xin công văn.","Vì đi đường vội vã nên chưa kịp xin công văn.","Khổng Tú","",},
        [3114] = {5103,"Khổng Tú","Không có bằng chứng, đợi ta bẩm báo Thừa Tướng mới có thể thả.","Không có bằng chứng, đợi ta bẩm báo Thừa Tướng mới có thể thả.","Quan Vũ","",},
        [3115] = {5103,"Quan Vũ","Đợi bẩm báo sẽ làm lỡ mất hành trình.","Đợi bẩm báo sẽ làm lỡ mất hành trình.","Khổng Tú","",},
        [3116] = {5103,"Khổng Tú","Phép tắc bắt buộc, phải như thế thôi.","Phép tắc bắt buộc, phải như thế thôi.","Quan Vũ","",},
        [3117] = {5103,"Quan Vũ","Ngươi không cho ta qua cửa quan phải không?","Ngươi không cho ta qua cửa quan phải không?","Khổng Tú","",},
        [3118] = {5103,"Khổng Tú","Muốn đi thì phải để lại già trẻ làm con tin.","Muốn đi thì phải để lại già trẻ làm con tin.","Quan Vũ","",},
        [3119] = {5103,"Quan Vũ","Thật ngông cuồng, xem đao.","Thật ngông cuồng, xem đao.","Khổng Tú","",},
        [3120] = {5104,"Hàn Phúc","Người đến là ai?","Người đến là ai?","Quan Vũ","",},
        [3121] = {5104,"Quan Vũ","Ta là Hán Thọ Đình Hầu Quan Vũ, xin mượn đường đi qua.","Ta là Hán Thọ Đình Hầu Quan Vũ, xin mượn đường đi qua.","Hàn Phúc","",},
        [3122] = {5104,"Hàn Phúc","Có công văn của thừa tướng không?","Có công văn của thừa tướng không?","Quan Vũ","",},
        [3123] = {5104,"Quan Vũ","Ta bận việc chưa kịp xin lấy.","Ta bận việc chưa kịp xin lấy.","Hàn Phúc","",},
        [3124] = {5104,"Hàn Phúc","Ta phụng mệnh thừa tướng trấn thủ nơi này để khám xét kẻ gian đi lại. Nếu không có công văn tức là trốn chạy.","Ta phụng mệnh thừa tướng trấn thủ nơi này để khám xét kẻ gian đi lại. Nếu không có công văn tức là trốn chạy.","Quan Vũ","",},
        [3125] = {5104,"Quan Vũ","Khổng Tú vừa bị ta diệt, ngươi cũng không muốn sống sao?","Khổng Tú vừa bị ta diệt, ngươi cũng không muốn sống sao?","Hàn Phúc","",},
        [3126] = {5104,"Quan Vũ","Xem đao!","Xem đao!","Hàn Phúc","",},
        [3127] = {5105,"Biện Hỉ","Tướng quân vang danh thiên hạ, ai cũng kính ngưỡng! Nay trở về với hoàng thúc là sự trung nghĩa!","Tướng quân vang danh thiên hạ, ai cũng kính ngưỡng! Nay trở về với hoàng thúc là sự trung nghĩa!","Quan Vũ","",},
        [3128] = {5105,"Quan Vũ","Nhưng ta vừa trảm sát Khổng Tú, Hàn Phúc!","Nhưng ta vừa trảm sát Khổng Tú, Hàn Phúc!","Biện Hỉ","",},
        [3129] = {5105,"Biện Hỉ","Tướng quân làm đúng, ta sẽ trình bày uẩn khúc với thừa tướng.","Tướng quân làm đúng, ta sẽ trình bày uẩn khúc với thừa tướng.","Quan Vũ","",},
