        [3131] = {5105,"Biện Hỉ","Vân Trường có muốn vào Trấn Quốc Tự uống trà, trong tự có một tăng nhân cùng quê hương với Vân Trường.","Vân Trường có muốn vào Trấn Quốc Tự uống trà, trong tự có một tăng nhân cùng quê hương với Vân Trường.","Quan Vũ","",},
        [3132] = {5105,"Quan Vũ","Rất tốt.","Rất tốt.","Biện Hỉ","",},
        [3133] = {5106,"Quan Vũ","Biện quân mời quan mỗ là có ý tốt hay ý xấu?","Biện quân mời quan mỗ là có ý tốt hay ý xấu?","Biện Hỉ","",},
        [3134] = {5106,"Biện Hỉ","Đương nhiên là ý tốt.","Đương nhiên là ý tốt.","Quan Vũ","",},
        [3135] = {5106,"Quan Vũ","Ta nghĩ ngươi là người tốt, ai ngờ ngươi dám làm vậy!","Ta nghĩ ngươi là người tốt, ai ngờ ngươi dám làm vậy!","Biện Hỉ","",},
        [3136] = {5106,"Quan Vũ","Ta đã thấy đám Đao Phủ Thủ đang ẩn thân rồi. (Nói xong giận dữ trảm Đao Phủ Thủ và Biện Hỉ.)","Ta đã thấy đám Đao Phủ Thủ đang ẩn thân rồi. (Nói xong giận dữ trảm Đao Phủ Thủ và Biện Hỉ.)","Biện Hỉ","",},
        [3137] = {5107,"Vương Thực","Tướng quân đi đường vất vả, hai phu nhân trên xe mỏi mệt, xin mời vào thành tạm nghỉ một đêm, ngày mai lên đường tiếp.","Tướng quân đi đường vất vả, hai phu nhân trên xe mỏi mệt, xin mời vào thành tạm nghỉ một đêm, ngày mai lên đường tiếp.","Quan Vũ","",},
        [3138] = {5107,"Quan Vũ","Rất tốt, xin mời đi trước, ta với nhị tẩu theo sau.","Rất tốt, xin mời đi trước, ta với nhị tẩu theo sau.","Vương Thực","",},
        [3139] = {5107,"Nhân vật","Tuyệt đối không thể!","Tuyệt đối không thể!","Quan Vũ","",},
