        [3141] = {5107,"Quan Vũ","Là thế nào?","Là thế nào?","Nhân vật","",},
        [3142] = {5107,"Nhân vật","Ta thấy xung quanh quán trọ rải đầy vật dễ cháy, chắc chắn hắn ta muốn đốt lửa thiêu quán trọ.","Ta thấy xung quanh quán trọ rải đầy vật dễ cháy, chắc chắn hắn ta muốn đốt lửa thiêu quán trọ.","Quan Vũ","",},
        [3143] = {5107,"Quan Vũ","Dương Uy Tướng hãy hộ tống nhị tẩu ra thành trước, ta đi diệt tên Vương Thực đó.","Dương Uy Tướng hãy hộ tống nhị tẩu ra thành trước, ta đi diệt tên Vương Thực đó.","Nhân vật","",},
