        [3157] = {5109,"Hạ Hầu Đôn","Thừa tướng không có công văn truyền báo. Ngươi dọc đường trảm tướng, rất là vô lễ. Ta quyết bắt ngươi giải về để thừa tướng xét xử!","Thừa tướng không có công văn truyền báo. Ngươi dọc đường trảm tướng, rất là vô lễ. Ta quyết bắt ngươi giải về để thừa tướng xét xử!","Quan Vũ","",},
        [3158] = {5110,"Trương Liêu","Vân Trường, Nguyên Nhượng đừng đánh nhau nữa!","Vân Trường, Nguyên Nhượng đừng đánh nhau nữa!","","",},
        [3159] = {5110,"Trương Liêu","Phụng chỉ thừa tướng: Vì biết Vân Trường vượt ải trảm tướng, sợ Vân Trường đi đường bị ngăn trở, nên sai ta đi truyền lời cho các ải cứ để cho Vân Trường đi.","Phụng chỉ thừa tướng: Vì biết Vân Trường vượt ải trảm tướng, sợ Vân Trường đi đường bị ngăn trở, nên sai ta đi truyền lời cho các ải cứ để cho Vân Trường đi.","","",},
        [3160] = {5110,"Hạ Hầu Đôn","Tần Kỳ là cháu của Thái Dương, hẳn gửi Tần Kỳ ở chỗ ta, nay bị Quan mỗ sát hại, sao hắn chịu để yên?","Tần Kỳ là cháu của Thái Dương, hẳn gửi Tần Kỳ ở chỗ ta, nay bị Quan mỗ sát hại, sao hắn chịu để yên?","Trương Liêu","",},
        [3161] = {5110,"Trương Liêu","Ta gặp Sái tướng quân sẽ có cách hòa giải. Thừa tướng khoan dung để cho Vân Trường đi, ngươi không nên trái lệnh.","Ta gặp Sái tướng quân sẽ có cách hòa giải. Thừa tướng khoan dung để cho Vân Trường đi, ngươi không nên trái lệnh.","Hạ Hầu Đôn","",},
        [3162] = {5110,"Hạ Hầu Đôn","Hừ, nếu thừa tướng đã nói vậy, ta biết làm sao được!","Hừ, nếu thừa tướng đã nói vậy, ta biết làm sao được!","Trương Liêu","",},
