        [3175] = {5202,"Quan Vũ","Ngươi là ai?","Ngươi là ai?","Liêu Hóa","",},
        [3176] = {5202,"Liêu Hóa","Ta họ Liêu tên Hoá, tự Nguyên Kiệm, vốn người Tương Dương, vì thời loạn mà lưu lạc giang hồ, tụ tập được hơn năm trăm người, cướp bóc kiếm ăn.","Ta họ Liêu tên Hoá, tự Nguyên Kiệm, vốn người Tương Dương, vì thời loạn mà lưu lạc giang hồ, tụ tập được hơn năm trăm người, cướp bóc kiếm ăn.","Quan Vũ","",},
