        [3371] = {5801,"Tào Tháo","Theo tin báo, quân Thiệu đã lui khoảng 30 dặm, đại tướng Hàn Mãnh sớm tối vận chuyển lương thảo đến tiếp tế.","Theo tin báo, quân Thiệu đã lui khoảng 30 dặm, đại tướng Hàn Mãnh sớm tối vận chuyển lương thảo đến tiếp tế.","Tuân Du","",},
        [3372] = {5801,"Tuân Du","Hàn Mãnh chỉ là kẻ hữu dũng vô mưu. Nếu phái người mang theo vài ngàn khinh kỵ tập kích giữa đường để cắt đứt lương thảo, quân Thiệu ắt sẽ loạn.","Hàn Mãnh chỉ là kẻ hữu dũng vô mưu. Nếu phái người mang theo vài ngàn khinh kỵ tập kích giữa đường để cắt đứt lương thảo, quân Thiệu ắt sẽ loạn.","Tào Tháo","",},
        [3373] = {5801,"Tào Tháo","Ai nguyện đi?","Ai nguyện đi?","Tuân Du","",},
