        [3381] = {5803,"Hứa Du","Tào Tháo đóng quân ở Quan Độ đã lâu, Hứa Xương chắc chắn đang trống không. Nếu phái một nhóm quân tập kích Hứa Xương, chắc chắn có thể đoạt Hứa Xương và bắt Tào Tháo.","Tào Tháo đóng quân ở Quan Độ đã lâu, Hứa Xương chắc chắn đang trống không. Nếu phái một nhóm quân tập kích Hứa Xương, chắc chắn có thể đoạt Hứa Xương và bắt Tào Tháo.","Viên Thiệu","",},
        [3382] = {5803,"Hứa Du","Nay Tào Tháo lương thảo đã hết, đây là cơ hội tốt, chia hai đường tấn công.","Nay Tào Tháo lương thảo đã hết, đây là cơ hội tốt, chia hai đường tấn công.","Viên Thiệu","",},
