        [3384] = {5803,"Hứa Du","Nay không đoạt, mai này sẽ bị phản công.","Nay không đoạt, mai này sẽ bị phản công.","Viên Thiệu","",},
        [3385] = {5803,"Viên Thiệu","Ngươi ở Ký Châu thường xuyên cướp bóc của bá tánh, đặt ra sưu cao thuế nặng, tiền bạc đều vào túi riêng.","Ngươi ở Ký Châu thường xuyên cướp bóc của bá tánh, đặt ra sưu cao thuế nặng, tiền bạc đều vào túi riêng.","Hứa Du","",},
        [3386] = {5803,"Viên Thiệu","Ngươi còn mặt mũi hiến kế trước mặt ta sao?","Ngươi còn mặt mũi hiến kế trước mặt ta sao?","Hứa Du","",},
        [3387] = {5803,"Viên Thiệu","Ngươi và Tào Tháo có giao tình, chắc nay lại được hắn hối lộ làm gian tế cho hắn!","Ngươi và Tào Tháo có giao tình, chắc nay lại được hắn hối lộ làm gian tế cho hắn!","Hứa Du","",},
        [3388] = {5803,"Viên Thiệu","Vốn định trảm thủ, nhưng nương tình tạm để lại thủ cấp cho ngươi! Hay mau lui đi!","Vốn định trảm thủ, nhưng nương tình tạm để lại thủ cấp cho ngươi! Hay mau lui đi!","Hứa Du","",},
        [3389] = {5803,"Hứa Du","Lời thật mất lòng, ngươi không đủ tư cách bàn việc lớn với ta!","Lời thật mất lòng, ngươi không đủ tư cách bàn việc lớn với ta!","Viên Thiệu","",},
        [3390] = {5804,"Tào Tháo","Đã khuya rồi sao người lại đến đây?","Đã khuya rồi sao người lại đến đây?","Hứa Du","",},
        [3391] = {5804,"Hứa Du","Ta ở bên Viên Thiệu, nói gì hắn cũng không nghe. Ta đến đây mong người thu nhận.","Ta ở bên Viên Thiệu, nói gì hắn cũng không nghe. Ta đến đây mong người thu nhận.","Tào Tháo","",},
        [3392] = {5804,"Tào Tháo","Tử Viễn đến đây, việc ta ắt thành! Mong người hãy hiến kế phá Thiệu.","Tử Viễn đến đây, việc ta ắt thành! Mong người hãy hiến kế phá Thiệu.","Hứa Du","",},
        [3393] = {5804,"Hứa Du","Lương thảo của ngài đủ dùng trong bao lâu?","Lương thảo của ngài đủ dùng trong bao lâu?","Tào Tháo","",},
        [3394] = {5804,"Tào Tháo","Một năm.","Một năm.","Hứa Du","",},
        [3395] = {5804,"Hứa Du","Ta có thành ý đến giúp mà ngài lại che giấu, ta cũng không biết phải làm sao!","Ta có thành ý đến giúp mà ngài lại che giấu, ta cũng không biết phải làm sao!","Tào Tháo","",},
        [3396] = {5804,"Hứa Du","Đừng giấu ta! Lương thảo đã hết rồi đúng không?","Đừng giấu ta! Lương thảo đã hết rồi đúng không?","Tào Tháo","",},
        [3397] = {5804,"Tào Tháo","Đúng là không thể qua mắt được Tử Viễn, mong người hãy nghĩ tình xưa mà hiến kế.","Đúng là không thể qua mắt được Tử Viễn, mong người hãy nghĩ tình xưa mà hiến kế.","Hứa Du","",},
        [3398] = {5804,"Hứa Du","Ta có một kế, trong vòng ba ngày, quân lính Viên Thiệu không chiến tự bại. Chúa công có muốn nghe không?","Ta có một kế, trong vòng ba ngày, quân lính Viên Thiệu không chiến tự bại. Chúa công có muốn nghe không?","Tào Tháo","",},
        [3399] = {5804,"Tào Tháo","Xin hãy nói.","Xin hãy nói.","Hứa Du","",},
        [3400] = {5804,"Hứa Du","Lương thảo của Viên Thiệu đều ở Ô Sào, hắn để Thuần Vu Quỳnh trấn giữ, nhưng Quỳnh lại rất thích uống rượu.","Lương thảo của Viên Thiệu đều ở Ô Sào, hắn để Thuần Vu Quỳnh trấn giữ, nhưng Quỳnh lại rất thích uống rượu.","Tào Tháo","",},
        [3401] = {5804,"Hứa Du","Ngài có thể chọn tinh binh giả làm lính Viên Thiệu đến đó bảo vệ lương thảo, rồi thừa cơ phóng hỏa, trong vòng ba ngày quân Thiệu ắt sẽ đại loạn.","Ngài có thể chọn tinh binh giả làm lính Viên Thiệu đến đó bảo vệ lương thảo, rồi thừa cơ phóng hỏa, trong vòng ba ngày quân Thiệu ắt sẽ đại loạn.","Tào Tháo","",},
        [3402] = {5805,"Trương Liêu","Nơi Viên Thiệu để lương thảo? Thừa tướng đừng vội tin, e rằng Hứa Du đang nói dối.","Nơi Viên Thiệu để lương thảo? Thừa tướng đừng vội tin, e rằng Hứa Du đang nói dối.","Tào Tháo","",},
        [3403] = {5805,"Tào Tháo","Không đâu, Hứa Du đến đây, trời diệt Viên Thiệu.","Không đâu, Hứa Du đến đây, trời diệt Viên Thiệu.","Trương Liêu","",},
        [3404] = {5805,"Tào Tháo","Nay lương thảo quân ta đã hết, khó mà kéo dài. Nếu không dùng kế của Hứa Du, cũng chỉ biết ngồi chờ.","Nay lương thảo quân ta đã hết, khó mà kéo dài. Nếu không dùng kế của Hứa Du, cũng chỉ biết ngồi chờ.","Trương Liêu","",},
        [3405] = {5805,"Tào Tháo","Nếu sợ có âm mưu thì có thể giữ ta lại ở đây. Việc cướp lương là diệu kế, mong chúa công đừng do dự.","Nếu sợ có âm mưu thì có thể giữ ta lại ở đây. Việc cướp lương là diệu kế, mong chúa công đừng do dự.","Trương Liêu","",},
        [3406] = {5805,"Trương Liêu","Cũng phải đề phòng Viên Thiệu thừa cơ tập kích.","Cũng phải đề phòng Viên Thiệu thừa cơ tập kích.","Tào Tháo","",},
        [3407] = {5805,"Tào Tháo","Ta đã trù tính cả rồi.","Ta đã trù tính cả rồi.","Trương Liêu","",},
        [3408] = {5805,"Tào Tháo","Tuân Du, Giả Hủ, Tào Hồng cùng Hứa Du trấn giữ ở trại.","Tuân Du, Giả Hủ, Tào Hồng cùng Hứa Du trấn giữ ở trại.","Trương Liêu","",},
        [3409] = {5805,"Tào Tháo","Hạ Hầu Đôn, Hạ Hầu Uyên dẫn quân mai phục ở bên trái, Tào Nhân, Lý Điển dẫn quân mai phục bên phải.","Hạ Hầu Đôn, Hạ Hầu Uyên dẫn quân mai phục ở bên trái, Tào Nhân, Lý Điển dẫn quân mai phục bên phải.","Trương Liêu","",},
        [3410] = {5805,"Tào Tháo","Trương Liêu, Hứa Chử ở trước, Từ Hoảng, Vu Cấm ở sau, ta và Dương Uy Tướng ở giữa: tổng cộng năm ngàn binh mã.","Trương Liêu, Hứa Chử ở trước, Từ Hoảng, Vu Cấm ở sau, ta và Dương Uy Tướng ở giữa: tổng cộng năm ngàn binh mã.","Trương Liêu","",},
