        [3416] = {5901,"Quách Đồ","Không được! Quân Tào cướp lương, Tào Tháo chắc chắn sẽ đích thân đến đó. Tháo đã đi, vậy trong doanh trại chắc chắn trống không.","Không được! Quân Tào cướp lương, Tào Tháo chắc chắn sẽ đích thân đến đó. Tháo đã đi, vậy trong doanh trại chắc chắn trống không.","Trương Hợp","",},
        [3417] = {5901,"Quách Đồ","Chúng ta có thể tấn công doanh trại Tào Tháo trước, Tào tháo nghe tin chắc chắn sẽ trở về.","Chúng ta có thể tấn công doanh trại Tào Tháo trước, Tào tháo nghe tin chắc chắn sẽ trở về.","Trương Hợp","",},
        [3418] = {5901,"Trương Hợp","Không được! Tào Tháo đa mưu, nếu đã ra ngoài chắc chắn đã cho người canh giữ cẩn mật.","Không được! Tào Tháo đa mưu, nếu đã ra ngoài chắc chắn đã cho người canh giữ cẩn mật.","Quách Đồ","",},
