        [3421] = {5901,"Viên Thiệu","Hai vị đừng tranh cãi nữa, Trương Hợp, Cao Lãm đưa 5000 quân đến Quan Độ tấn công Tào doanh, Tưởng Kỳ đưa 1 vạn quân đi cứu Ô Sào.","Hai vị đừng tranh cãi nữa, Trương Hợp, Cao Lãm đưa 5000 quân đến Quan Độ tấn công Tào doanh, Tưởng Kỳ đưa 1 vạn quân đi cứu Ô Sào.","Quách Đồ","",},
        [3422] = {5902,"Viên Thiệu","Sao lại để mất Ô Sào? Đã điều tra kỹ chưa?","Sao lại để mất Ô Sào? Đã điều tra kỹ chưa?","Quách Đồ","",},
