        [3426] = {5902,"Viên Thiệu","Sao lại nói thế?","Sao lại nói thế?","Quách Đồ","",},
        [3427] = {5902,"Quách Đồ","Hai người họ sớm có ý hàng Tào, nay sai đi tập kích Tào doanh, cố tình để thua để ta hao binh tổn tướng.","Hai người họ sớm có ý hàng Tào, nay sai đi tập kích Tào doanh, cố tình để thua để ta hao binh tổn tướng.","Viên Thiệu","",},
        [3428] = {5902,"Viên Thiệu","Sao? Mau gọi Trương Hợp, Cao Lãm về đây!","Sao? Mau gọi Trương Hợp, Cao Lãm về đây!","Quách Đồ","",},
