        [3510] = {6401,"Tào Tháo","Theo binh đến Ký Châu, nhưng Viên Đàm và Viên Thượng vào thành cố thủ, Viên Hi và Cao Can rời thành 30 dặm đóng trại, tấn công liên tiếp không được.","Theo binh đến Ký Châu, nhưng Viên Đàm và Viên Thượng vào thành cố thủ, Viên Hi và Cao Can rời thành 30 dặm đóng trại, tấn công liên tiếp không được.","Quách Gia","",},
        [3511] = {6401,"Tào Tháo","Phải làm sao đây?","Phải làm sao đây?","Quách Gia","",},
        [3512] = {6401,"Quách Gia","Viên thị phế trưởng lập ấu, giữa huynh đệ thì quyền lực ngang nhau, tự lập bè cánh, lúc nguy cấp thì giúp đỡ nhau, lúc yên ổn thì tranh đấu nhau.","Viên thị phế trưởng lập ấu, giữa huynh đệ thì quyền lực ngang nhau, tự lập bè cánh, lúc nguy cấp thì giúp đỡ nhau, lúc yên ổn thì tranh đấu nhau.","Tào Tháo","",},
        [3513] = {6401,"Quách Gia","Hay là dẫn binh tiến về Kinh Châu, chinh phạt Lưu Biểu, sau này huynh đệ Viên Thị có biến, lúc đó tấn công họ, có thể đạt mục đích.","Hay là dẫn binh tiến về Kinh Châu, chinh phạt Lưu Biểu, sau này huynh đệ Viên Thị có biến, lúc đó tấn công họ, có thể đạt mục đích.","Tào Tháo","",},
        [3514] = {6401,"Tào Tháo","Phụng Hiếu nói phải!","Phụng Hiếu nói phải!","Quách Gia","",},
        [3515] = {6401,"Tào Tháo","Lệnh Giả Hủ làm Thái Thú, thủ Lê Dương, Tào Hồng dẫn binh thủ Quan Độ, dẫn đại quân tiến về Kinh Châu.","Lệnh Giả Hủ làm Thái Thú, thủ Lê Dương, Tào Hồng dẫn binh thủ Quan Độ, dẫn đại quân tiến về Kinh Châu.","Quách Gia","",},
        [3516] = {6402,"Viên Đàm","Ta là con cả lại không được kế thừa nghiệp cha; con của mẹ kế lại được ban chức tước, thực sự không cam tâm.","Ta là con cả lại không được kế thừa nghiệp cha; con của mẹ kế lại được ban chức tước, thực sự không cam tâm.","Quách Đồ","",},
        [3517] = {6402,"Quách Đồ","Chúa công có thể đóng quân ngoài thành, chỉ mời Hiển Phủ, Thẩm Phối uống rượu, Đao Phủ Thủ mai phục tiêu diệt, đại sự được định.","Chúa công có thể đóng quân ngoài thành, chỉ mời Hiển Phủ, Thẩm Phối uống rượu, Đao Phủ Thủ mai phục tiêu diệt, đại sự được định.","Viên Đàm","",},
        [3518] = {6403,"Thẩm Phối","Viên Đàm mời gặp riêng ngươi riêng, chắc chắn là kế của Quách Đồ. Chúa công nếu đi chắc chắn trúng gian kế, hay là thừa thế tấn công","Viên Đàm mời gặp riêng ngươi riêng, chắc chắn là kế của Quách Đồ. Chúa công nếu đi chắc chắn trúng gian kế, hay là thừa thế tấn công","Viên Thượng","",},
