        [3529] = {6405,"Tào Tháo","Không ngờ sự tình bại lộ, tinh binh Thẩm Phối vận chuyển đá đến tập kích ở miệng cống, Phùng Lễ và 300 tráng sĩ đều đã hy sinh.","Không ngờ sự tình bại lộ, tinh binh Thẩm Phối vận chuyển đá đến tập kích ở miệng cống, Phùng Lễ và 300 tráng sĩ đều đã hy sinh.","","",},
        [3530] = {6405,"Tào Tháo","Còn có kế nào không?","Còn có kế nào không?","","",},
        [3531] = {6405,"Hứa Du","Ta có một kế, sao không dùng nước Chương Hà để dìm chúng?","Ta có một kế, sao không dùng nước Chương Hà để dìm chúng?","Tào Tháo","",},
        [3532] = {6405,"Tào Tháo","Xem ra khả thi, vậy làm theo kế của Tử Viễn","Xem ra khả thi, vậy làm theo kế của Tử Viễn","Hứa Du","",},
        [3533] = {6406,"Viên Thượng","Dẫn quân đào rãnh ở ngoại thành!","Dẫn quân đào rãnh ở ngoại thành!","Thẩm Phối","",},
        [3534] = {6406,"Thẩm Phối","Chờ ta xem nào.","Chờ ta xem nào.","Viên Thượng","",},
        [3535] = {6406,"Thẩm Phối","Làm vậy để lấy nước Chương Hà tràn vào thành. Rãnh sâu mới có thể tràn, nông như vậy thì có tác dụng gì!","Làm vậy để lấy nước Chương Hà tràn vào thành. Rãnh sâu mới có thể tràn, nông như vậy thì có tác dụng gì!","Viên Thượng","",},
        [3536] = {6407,"Hứa Du","Thẩm Phối sơ ý, quân ta trong đêm đã phái thêm lượng binh sĩ gấp 10 lần cùng đào, đợi đến trời sáng sẽ đào sâu hai trượng.","Thẩm Phối sơ ý, quân ta trong đêm đã phái thêm lượng binh sĩ gấp 10 lần cùng đào, đợi đến trời sáng sẽ đào sâu hai trượng.","Tào Tháo","",},
        [3537] = {6407,"Hứa Du","Dẫn nước Chương Hà vào, trong thành nước ngập lại thiếu lương thực, quân sĩ sẽ chết đói.","Dẫn nước Chương Hà vào, trong thành nước ngập lại thiếu lương thực, quân sĩ sẽ chết đói.","Tào Tháo","",},
        [3538] = {6407,"Tào Tháo","Tốt! Tướng địch Thẩm Vinh chịu đầu hàng, mở cổng Tây cho binh ta vào.","Tốt! Tướng địch Thẩm Vinh chịu đầu hàng, mở cổng Tây cho binh ta vào.","Hứa Du","",},
        [3539] = {6407,"Tào Tháo","Ra lệnh toàn quân, tấn công Ký Châu!","Ra lệnh toàn quân, tấn công Ký Châu!","Hứa Du","",},
        [3540] = {6408,"Tào Tháo","Khanh trung thành với Viên thị, không thể không làm vậy. Nay chịu đầu hàng ta không?","Khanh trung thành với Viên thị, không thể không làm vậy. Nay chịu đầu hàng ta không?","Thẩm Phối","",},
        [3541] = {6408,"Thẩm Phối","Không hàng!","Không hàng!","Tào Tháo","",},
        [3542] = {6408,"Thẩm Phối","Ta là người của Viên thị, không như lũ tặc a dua nịnh hót các ngươi! Muốn chém cứ chém!","Ta là người của Viên thị, không như lũ tặc a dua nịnh hót các ngươi! Muốn chém cứ chém!","Tào Tháo","",},
