        [3544] = {6501,"Tào Phi","Hai người phụ nữ đang khóc là ai? (Tào Phi vào Viên Phủ thấy hai người phụ nữ khóc bèn hỏi.)","Hai người phụ nữ đang khóc là ai? (Tào Phi vào Viên Phủ thấy hai người phụ nữ khóc bèn hỏi.)","Lưu Phu Nhân","",},
        [3545] = {6501,"Lưu Phu Nhân","Thiếp là Lưu Thị, thê tử Viên Tướng Quân.","Thiếp là Lưu Thị, thê tử Viên Tướng Quân.","Tào Phi","",},
        [3546] = {6501,"Tào Phi","Người phụ nữ này là ai?","Người phụ nữ này là ai?","Lưu Phu Nhân","",},
        [3547] = {6501,"Lưu Phu Nhân","Đây là Chân Thị, thê tử của con thứ Viên Hi. Do Viên Hi rời trấn, Chân Thị không muốn đi xa nên ở lại.","Đây là Chân Thị, thê tử của con thứ Viên Hi. Do Viên Hi rời trấn, Chân Thị không muốn đi xa nên ở lại.","Tào Phi","",},
        [3548] = {6501,"Tào Phi","Ta là con trai Tào Thừa Tướng, sẽ bảo vệ gia đình ngươi, đừng lo lắng.","Ta là con trai Tào Thừa Tướng, sẽ bảo vệ gia đình ngươi, đừng lo lắng.","Lưu Phu Nhân","",},
        [3549] = {6502,"Hứa Du","Không có ta, sao ngươi có thể vào cổng này?","Không có ta, sao ngươi có thể vào cổng này?","Tào Tháo","",},
        [3550] = {6502,"Tào Tháo","Ha ha ha ha, nào, cùng vào Cổng Thành.","Ha ha ha ha, nào, cùng vào Cổng Thành.","Hứa Du","",},
        [3551] = {6502,"Tào Tháo","Chúng ta đến Thiệu Phủ trước.","Chúng ta đến Thiệu Phủ trước.","Hứa Du","",},
        [3552] = {6502,"Tào Tháo","Ai từng vào cổng này?","Ai từng vào cổng này?","Hứa Du","",},
        [3553] = {6502,"Từ Hoảng","Thế tử ở trong đó.","Thế tử ở trong đó.","Tào Tháo","",},
        [3554] = {6502,"Tào Tháo","Gọi hắn ra đây! Sao có thể tự ý vào trong!","Gọi hắn ra đây! Sao có thể tự ý vào trong!","Từ Hoảng","",},
        [3555] = {6502,"Tào Phi","Chân Thị dung mạo như hoa, sắc đẹp khuynh quốc, ta muốn có nàng!","Chân Thị dung mạo như hoa, sắc đẹp khuynh quốc, ta muốn có nàng!","","",},
        [3556] = {6502,"Lưu Phu Nhân","Ngoài thế tử ra không ai bảo toàn được gia đình thiếp, hãy để Chân Thị làm thê tử thế tử.","Ngoài thế tử ra không ai bảo toàn được gia đình thiếp, hãy để Chân Thị làm thê tử thế tử.","Tào Tháo","",},
        [3557] = {6502,"Tào Tháo","Được! Ta cho phép.","Được! Ta cho phép.","Lưu Phu Nhân","",},
        [3558] = {6503,"Tào Tháo","Ngày nào ta và Bản Sơ kết giao huynh đệ, nay y đã qua đời, ta không thể không rơi lệ!","Ngày nào ta và Bản Sơ kết giao huynh đệ, nay y đã qua đời, ta không thể không rơi lệ!","","",},
        [3559] = {6503,"Tào Tháo","Ban lụa vàng lương thực cho Lưu Thị, nay cư dân Hà Bắc chịu khổ vì chiến tranh, miễn thuế năm nay.","Ban lụa vàng lương thực cho Lưu Thị, nay cư dân Hà Bắc chịu khổ vì chiến tranh, miễn thuế năm nay.","","",},
        [3560] = {6504,"Hứa Du","Các ngươi không có ta, sao có thể ra vào cổng này?","Các ngươi không có ta, sao có thể ra vào cổng này?","Hứa Chử","",},
        [3561] = {6504,"Hứa Chử","Chúng ta trải qua trận chiến sinh tử đoạt được thành trì, sao ngươi dám khoác lác!","Chúng ta trải qua trận chiến sinh tử đoạt được thành trì, sao ngươi dám khoác lác!","Hứa Du","",},
        [3562] = {6504,"Hứa Du","Các ngươi đều là thất phu, không đáng quan tâm!","Các ngươi đều là thất phu, không đáng quan tâm!","Hứa Chử","",},
        [3563] = {6504,"Hứa Chử","Vô lễ, xem ra ngươi chán sống rồi!","Vô lễ, xem ra ngươi chán sống rồi!","Hứa Du","",},
        [3564] = {6505,"Hứa Chử","Hứa Du vô lễ, ta đã trừng trị hắn. (Trình thủ cấp Hứa Du.)","Hứa Du vô lễ, ta đã trừng trị hắn. (Trình thủ cấp Hứa Du.)","Tào Tháo","",},
        [3565] = {6505,"Tào Tháo","Tử Viễn và ta vốn quen biết, sao người dám xử quyết hắn!","Tử Viễn và ta vốn quen biết, sao người dám xử quyết hắn!","Hứa Chử","",},
        [3566] = {6505,"Hứa Chử","Kẻ ngông cuồng vô lễ, ta thấy quá chướng mắt nên đã trừng trị hắn!","Kẻ ngông cuồng vô lễ, ta thấy quá chướng mắt nên đã trừng trị hắn!","Tào Tháo","",},
        [3567] = {6505,"Tào Tháo","Lỗ mãng lỗ mãng! Ta lệnh ngươi đích thân an táng long trọng Hứa Du.","Lỗ mãng lỗ mãng! Ta lệnh ngươi đích thân an táng long trọng Hứa Du.","Hứa Chử","",},
        [3568] = {6601,"Tào Tháo","Đã định Ký Châu, Viên Đàm, Viên Thượng, Viên Hi có tin tức gì không?","Đã định Ký Châu, Viên Đàm, Viên Thượng, Viên Hi có tin tức gì không?","","",},
        [3569] = {6601,"Nhân vật","Không có lòng chiến đấu, trốn đến U Châu tìm Viên Hi.","Không có lòng chiến đấu, trốn đến U Châu tìm Viên Hi.","Tào Tháo","",},
        [3570] = {6601,"Nhân vật","Viên Đàm dẫn binh cướp Cam Ninh, An Bình, Hà Giản... muốn lấy lại Ký Châu.","Viên Đàm dẫn binh cướp Cam Ninh, An Bình, Hà Giản... muốn lấy lại Ký Châu.","Tào Tháo","",},
        [3571] = {6601,"Tào Tháo","Lệnh đại quân chinh phạt!","Lệnh đại quân chinh phạt!","Nhân vật","",},
        [3572] = {6602,"Nhân vật","Viên Đàm viết thư cầu Lưu Biểu tăng viện trợ, Lưu Biểu không có ý ra binh, tự biết không địch lại chúng ta, chạy đi giữ Nam Bì.","Viên Đàm viết thư cầu Lưu Biểu tăng viện trợ, Lưu Biểu không có ý ra binh, tự biết không địch lại chúng ta, chạy đi giữ Nam Bì.","Tào Tháo","",},
        [3573] = {6602,"Tào Tháo","Đuổi!","Đuổi!","Nhân vật","",},
        [3574] = {6603,"Nhân vật","Ta và Tào Hồng Phấn Uy đột trận, thì gặp Viên Đàm, vung đao loạn trảm, Đàm đã mất mạng trong chiến loạn,","Ta và Tào Hồng Phấn Uy đột trận, thì gặp Viên Đàm, vung đao loạn trảm, Đàm đã mất mạng trong chiến loạn,","Tào Tháo","",},
        [3575] = {6603,"Nhân vật","Quách Đồ thấy đại loạn vội vào thành, ta nhặt lấy cung tiễn, bắn hạ thành hào, người ngựa đều gục.","Quách Đồ thấy đại loạn vội vào thành, ta nhặt lấy cung tiễn, bắn hạ thành hào, người ngựa đều gục.","Tào Tháo","",},
        [3576] = {6603,"Tào Tháo","Hay! Dương Uy Tướng thần võ!","Hay! Dương Uy Tướng thần võ!","Nhân vật","",},
        [3577] = {6603,"Tào Tháo","Viên Đàm đã hạ, tiếp tục chinh phạt Viên Thượng Viên Hi!","Viên Đàm đã hạ, tiếp tục chinh phạt Viên Thượng Viên Hi!","Nhân vật","",},
        [3578] = {6604,"Tào Tháo","Nay Viên Thượng đã theo Viên Hi, phải dùng kế sách gì?","Nay Viên Thượng đã theo Viên Hi, phải dùng kế sách gì?","Quách Gia","",},
        [3579] = {6604,"Quách Gia","Có thể khiến Viên Thị đầu hàng tướng Tiêu Xúc, Trương Nam sẽ tự tấn công.","Có thể khiến Viên Thị đầu hàng tướng Tiêu Xúc, Trương Nam sẽ tự tấn công.","Tào Tháo","",},
        [3580] = {6604,"Tào Tháo","Tiêu Xúc, Trương Nam, Lữ Khoáng, Lữ Tường, Mã Diên, lần lượt dẫn theo bộ binh, chia làm ba ngã tấn công U Châu.","Tiêu Xúc, Trương Nam, Lữ Khoáng, Lữ Tường, Mã Diên, lần lượt dẫn theo bộ binh, chia làm ba ngã tấn công U Châu.","Quách Gia","",},
        [3581] = {6604,"Tào Tháo","Lý Điển, Nhạc Tiến tụ họp với Trương Yến, đánh Tịnh Châu, tấn công Cao Can.","Lý Điển, Nhạc Tiến tụ họp với Trương Yến, đánh Tịnh Châu, tấn công Cao Can.","Quách Gia","",},
        [3582] = {6605,"Tào Hồng","Cao Can không còn đường chạy, đành phải tìm đến Lưu Biểu, đi đến Thương Lạc, bị Đô Úy Vương Diễm hạ sát.","Cao Can không còn đường chạy, đành phải tìm đến Lưu Biểu, đi đến Thương Lạc, bị Đô Úy Vương Diễm hạ sát.","Tào Tháo","",},
        [3583] = {6605,"Nhân vật","Viên Hi, Viên Thượng bại binh vong tướng, thế lực cùng tận, chạy đến sa mạc.","Viên Hi, Viên Thượng bại binh vong tướng, thế lực cùng tận, chạy đến sa mạc.","Tào Tháo","",},
        [3584] = {6605,"Nhân vật","Ta dẫn binh tây kích, Lưu Bị, Lưu Biểu thừa cơ tấn công Hứa Đô, ta cứu ứng không kịp, gây họa không ít, hãy rút quân đừng vào.","Ta dẫn binh tây kích, Lưu Bị, Lưu Biểu thừa cơ tấn công Hứa Đô, ta cứu ứng không kịp, gây họa không ít, hãy rút quân đừng vào.","Tào Tháo","",},
        [3585] = {6605,"Quách Gia","Dương Uy Tướng nói sai rồi. Chúa công tuy uy chấn thiên hạ, người ở sa mạc xa xôi tách biệt, chắc chắn không phòng bị.","Dương Uy Tướng nói sai rồi. Chúa công tuy uy chấn thiên hạ, người ở sa mạc xa xôi tách biệt, chắc chắn không phòng bị.","Tào Tháo","",},
        [3586] = {6605,"Quách Gia","Nhân lúc không phòng bị, tấn công bất ngờ chắc chắn phá được. Viên Thiệu và Ô Hoàn có ơn, huynh đệ Thượng và Hi còn sống sót, không thể không diệt trừ.","Nhân lúc không phòng bị, tấn công bất ngờ chắc chắn phá được. Viên Thiệu và Ô Hoàn có ơn, huynh đệ Thượng và Hi còn sống sót, không thể không diệt trừ.","Tào Tháo","",},
        [3587] = {6605,"Quách Gia","Lưu Biểu tự biết tài cán không đủ để quản Lưu Bị, việc quan trọng sợ không thể kiểm soát, việc nhẹ nhàng lại không thích hợp Lưu Bị làm.","Lưu Biểu tự biết tài cán không đủ để quản Lưu Bị, việc quan trọng sợ không thể kiểm soát, việc nhẹ nhàng lại không thích hợp Lưu Bị làm.","Tào Tháo","",},
        [3588] = {6605,"Quách Gia","Tuy viễn chinh hư quốc, người cũng đừng lo lắng.","Tuy viễn chinh hư quốc, người cũng đừng lo lắng.","Tào Tháo","",},
        [3589] = {6605,"Tào Tháo","Phụng Hiếu nói rất phải. Lệnh tam quân lớn nhỏ, ngàn xe tiến lên.","Phụng Hiếu nói rất phải. Lệnh tam quân lớn nhỏ, ngàn xe tiến lên.","Quách Gia","",},
        [3590] = {6606,"Tào Tháo","Vì ta muốn bình định sa mạc, khiến người đi xa vất vả bị nhiễm bệnh, ta thật áy náy! (Thấy Quách Gia không quen nơi lạ, bệnh nằm trên xe.)","Vì ta muốn bình định sa mạc, khiến người đi xa vất vả bị nhiễm bệnh, ta thật áy náy! (Thấy Quách Gia không quen nơi lạ, bệnh nằm trên xe.)","Quách Gia","",},
        [3591] = {6606,"Quách Gia","Cảm kích đại ơn Thừa Tướng, dù có mất mạng cũng không đủ báo đáp người.","Cảm kích đại ơn Thừa Tướng, dù có mất mạng cũng không đủ báo đáp người.","Tào Tháo","",},
        [3592] = {6606,"Tào Tháo","Ta thấy Bắc Địa hiểm trở, định rút quân về, thế nào?","Ta thấy Bắc Địa hiểm trở, định rút quân về, thế nào?","Quách Gia","",},
        [3593] = {6606,"Quách Gia","Thần tốc. Thiên lý tấn công, quân nhu nhiều nhưng khó xu lợi, hay là dẫn binh đi gấp, lợi dụng lúc hắn không phòng bị.","Thần tốc. Thiên lý tấn công, quân nhu nhiều nhưng khó xu lợi, hay là dẫn binh đi gấp, lợi dụng lúc hắn không phòng bị.","Tào Tháo","",},
        [3594] = {6606,"Tào Tháo","Ngươi ở Dị Châu dưỡng bệnh, ta tự nhờ đạo quan dẫn đường.","Ngươi ở Dị Châu dưỡng bệnh, ta tự nhờ đạo quan dẫn đường.","Quách Gia","",},
        [3595] = {6607,"Tào Tháo","Phụng Hiếu qua đời, là trời muốn diệt ta rồi! (Chờ khi Tào Tháo đến Dị Châu, Quách Gia đã qua đời.)","Phụng Hiếu qua đời, là trời muốn diệt ta rồi! (Chờ khi Tào Tháo đến Dị Châu, Quách Gia đã qua đời.)","","",},
        [3596] = {6607,"Tào Tháo","Tuổi tác chư quân đều ngang với ta, chỉ có Phụng Hiếu nhỏ tuổi nhất, định nhờ vả hậu sự. Nào ngờ đoản mệnh, ruột gan ta đau như cắt!","Tuổi tác chư quân đều ngang với ta, chỉ có Phụng Hiếu nhỏ tuổi nhất, định nhờ vả hậu sự. Nào ngờ đoản mệnh, ruột gan ta đau như cắt!","","",},
