        [3814] = {7206,"Từ Thứ","Đó là cứu chủ, nhưng chắc chắn sẽ hại một chủ, ta có một cách để hóa giải.","Đó là cứu chủ, nhưng chắc chắn sẽ hại một chủ, ta có một cách để hóa giải.","Lưu Bị","",},
