        [3816] = {7206,"Từ Thứ","Ngài nếu có kẻ thù nào hãy tặng ngựa cho người đó, để nó hại kẻ kia xong rồi mới cưỡi sẽ an toàn.","Ngài nếu có kẻ thù nào hãy tặng ngựa cho người đó, để nó hại kẻ kia xong rồi mới cưỡi sẽ an toàn.","Lưu Bị","",},
        [3817] = {7206,"Lưu Bị","Ngươi lại đi bảo ta làm chuyện lợi mình hại người, Lưu Bị này không thể làm.","Ngươi lại đi bảo ta làm chuyện lợi mình hại người, Lưu Bị này không thể làm.","Từ Thứ","",},
