        [3860] = {7306,"Trình Dục","Từ Thứ chí hiếu. Chỉ còn mẹ già ở một mình.","Từ Thứ chí hiếu. Chỉ còn mẹ già ở một mình.","Tào Tháo","",},
        [3861] = {7306,"Trình Dục","Thừa tướng bảo người đón mẹ hắn đến Hứa Xương, bảo mẹ hắn viết thư gọi về, chắc chắn Từ Thứ sẽ đến.","Thừa tướng bảo người đón mẹ hắn đến Hứa Xương, bảo mẹ hắn viết thư gọi về, chắc chắn Từ Thứ sẽ đến.","Tào Tháo","",},
        [3862] = {7307,"Từ Thứ","Ta là Từ Thứ, do chạy nạn nên đổi tên thành Đơn Phúc. Sau được Lưu Cảnh Thăng chiêu nạp, sau khi gặp mới biết hắn là kẻ vô dụng, chỉ tỏ vẻ ta đây.","Ta là Từ Thứ, do chạy nạn nên đổi tên thành Đơn Phúc. Sau được Lưu Cảnh Thăng chiêu nạp, sau khi gặp mới biết hắn là kẻ vô dụng, chỉ tỏ vẻ ta đây.","Lưu Bị","",},
        [3863] = {7307,"Từ Thứ","Buổi tối đó đến chỗ Tư Mã Thủy Kính thì bị trách Thứ không biết chọn chủ mà phụng sự, nghe nói Lưu Dự Châu ở đây, sao không đi theo?","Buổi tối đó đến chỗ Tư Mã Thủy Kính thì bị trách Thứ không biết chọn chủ mà phụng sự, nghe nói Lưu Dự Châu ở đây, sao không đi theo?","Lưu Bị","",},
