        [3866] = {7307,"Từ Thứ","Luôn muốn góp sức cho chúa công, nhưng đạo hiếu vẫn còn, không thể tận lực. Nay phải từ biệt, mong sẽ gặp lại.","Luôn muốn góp sức cho chúa công, nhưng đạo hiếu vẫn còn, không thể tận lực. Nay phải từ biệt, mong sẽ gặp lại.","Lưu Bị","",},
        [3867] = {7307,"Lưu Bị","Hiếu với mẹ là lẽ thường. Hãy đến gặp mẹ trước, sau này chúng ta còn có dịp gặp nhau.","Hiếu với mẹ là lẽ thường. Hãy đến gặp mẹ trước, sau này chúng ta còn có dịp gặp nhau.","Từ Thứ","",},
        [3868] = {7308,"Tôn Càn","Nguyên Trực là kỳ tài, lại ở Tân Dã biết hết quân tình của ta. Nay đầu quân Tào Tháo, e quân ta gặp nguy.","Nguyên Trực là kỳ tài, lại ở Tân Dã biết hết quân tình của ta. Nay đầu quân Tào Tháo, e quân ta gặp nguy.","Lưu Bị","",},
        [3869] = {7308,"Tôn Càn","Chúa công nên giữ lại. Tháo thấy Nguyên Trực không đến sẽ trảm mẹ, Nguyên Trực chắc chắn vì mẹ báo thù kháng Tào.","Chúa công nên giữ lại. Tháo thấy Nguyên Trực không đến sẽ trảm mẹ, Nguyên Trực chắc chắn vì mẹ báo thù kháng Tào.","Lưu Bị","",},
