        [3894] = {7310,"Lưu Bị","May có tiên sinh nói cho biết! Lưu Bị đúng là không có tầm nhìn.","May có tiên sinh nói cho biết! Lưu Bị đúng là không có tầm nhìn.","Từ Thứ","",},
        [3895] = {7311,"Từ Thứ","Ta vốn muốn phụng sự cho ngài, nay do mẹ viết thư nên phải đi.","Ta vốn muốn phụng sự cho ngài, nay do mẹ viết thư nên phải đi.","Gia Cát Lượng","",},
        [3896] = {7311,"Từ Thứ","Nay tiến cử Khổng Minh cho Huyền Đức. Ngài ấy sẽ đến ngay, mong tiên sinh hết lòng phò trợ.","Nay tiến cử Khổng Minh cho Huyền Đức. Ngài ấy sẽ đến ngay, mong tiên sinh hết lòng phò trợ.","Gia Cát Lượng","",},
