        [3901] = {7312,"Gia Cát Lượng","Kẻ nhàn nơi Hoa Dương, khiến tướng quân về không mấy lần, thật xấu hổ.","Kẻ nhàn nơi Hoa Dương, khiến tướng quân về không mấy lần, thật xấu hổ.","Lưu Bị","",},
        [3902] = {7312,"Gia Cát Lượng","Ta thấy ý trong sách đã đủ biết tiên sinh lo nước lo dân, chỉ tiếc là Lượng đây bất tài, có điều cần hỏi.","Ta thấy ý trong sách đã đủ biết tiên sinh lo nước lo dân, chỉ tiếc là Lượng đây bất tài, có điều cần hỏi.","Lưu Bị","",},
        [3903] = {7312,"Lưu Bị","Lời của Tư Mã Đức Tháo, lời của Từ Thứ, ngài nghĩ thế nào? Mong tiên sinh chỉ giáo.","Lời của Tư Mã Đức Tháo, lời của Từ Thứ, ngài nghĩ thế nào? Mong tiên sinh chỉ giáo.","Gia Cát Lượng","",},
        [3904] = {7312,"Gia Cát Lượng","Đức Tháo, Nguyên Trực đều là nhân tài. Lượng chỉ là kẻ tầm thường, nào dám bàn luận? Sao tướng quân lại cần đến kẻ tầm thường này?","Đức Tháo, Nguyên Trực đều là nhân tài. Lượng chỉ là kẻ tầm thường, nào dám bàn luận? Sao tướng quân lại cần đến kẻ tầm thường này?","Lưu Bị","",},
        [3905] = {7312,"Lưu Bị","Xin tiên sinh hãy vì bá tánh mà xuống núi phò trợ. Lưu Bị xin được chỉ giáo.","Xin tiên sinh hãy vì bá tánh mà xuống núi phò trợ. Lưu Bị xin được chỉ giáo.","Gia Cát Lượng","",},
