        [4043] = {7607,"Lý Điển","Rừng rậm rạp, nếu chúng dùng hỏa công thì sao?","Rừng rậm rạp, nếu chúng dùng hỏa công thì sao?","Vu Cấm","",},
        [4044] = {7607,"Vu Cấm","Là lời chủ tướng. Ta tiến lên trước, chủ tướng dẫn hậu quân.","Là lời chủ tướng. Ta tiến lên trước, chủ tướng dẫn hậu quân.","Lý Điển","",},
        [4045] = {7607,"Vu Cấm","Hậu quân chậm lại! Tiền quân sắp dừng!","Hậu quân chậm lại! Tiền quân sắp dừng!","Lý Điển","",},
        [4046] = {7607,"Vu Cấm","Đường phía nam hiểm trở, có thể cản hỏa công.","Đường phía nam hiểm trở, có thể cản hỏa công.","Lý Điển","",},
        [4047] = {7607,"Hạ Hầu Đôn","Đứng lại!","Đứng lại!","Vu Cấm","",},
        [4048] = {7608,"Hạ Hầu Đôn","Lý Điển, Vu Cấm nói vậy rồi hối không kịp!","Lý Điển, Vu Cấm nói vậy rồi hối không kịp!","","",},
        [4049] = {7608,"Hạ Hầu Đôn","Nay nếm mùi thất bại, chạy về Hứa Xương.","Nay nếm mùi thất bại, chạy về Hứa Xương.","","",},
        [4050] = {7609,"Trương Phi","Khổng Minh thật anh kiệt!","Khổng Minh thật anh kiệt!","Gia Cát Lượng","",},
        [4051] = {7609,"Quan Vũ","Ta rất ngưỡng mộ.","Ta rất ngưỡng mộ.","Gia Cát Lượng","",},
        [4052] = {7609,"Gia Cát Lượng","Hạ Hầu Đôn bỏ chạy, Tào Tháo sẽ tự dẫn quân đến.","Hạ Hầu Đôn bỏ chạy, Tào Tháo sẽ tự dẫn quân đến.","Quan Vũ","",},
        [4053] = {7609,"Lưu Bị","Phải làm sao đây?","Phải làm sao đây?","Gia Cát Lượng","",},
        [4054] = {7609,"Gia Cát Lượng","Ta có kế này.","Ta có kế này.","Lưu Bị","",},
