        [4056] = {7609,"Gia Cát Lượng","Tân Dã là một huyện nhỏ, không ở lâu được. Mới đây, tôi nghe Lưu Biểu bị bệnh nguy cấp lắm, chúa công phải nhân dịp này chiếm lấy Kinh Châu làm căn cứ đã, rồi hãy chống cự Tào Tháo sau","Tân Dã là một huyện nhỏ, không ở lâu được. Mới đây, tôi nghe Lưu Biểu bị bệnh nguy cấp lắm, chúa công phải nhân dịp này chiếm lấy Kinh Châu làm căn cứ đã, rồi hãy chống cự Tào Tháo sau","Lưu Bị","",},
        [4057] = {7609,"Lưu Bị","Kế của tiên sinh hay lắm nhưng tôi đã chịu ơn Cảnh Thăng, không nỡ làm thế!","Kế của tiên sinh hay lắm nhưng tôi đã chịu ơn Cảnh Thăng, không nỡ làm thế!","Gia Cát Lượng","",},
        [4058] = {7609,"Gia Cát Lượng","Nếu chúa công không lấy bây giờ, sau sẽ hối không kịp.","Nếu chúa công không lấy bây giờ, sau sẽ hối không kịp.","Lưu Bị","",},
        [4059] = {7609,"Lưu Bị","Ta thà chết, chứ không làm chuyện bất nghĩa","Ta thà chết, chứ không làm chuyện bất nghĩa","Gia Cát Lượng","",},
        [4060] = {7609,"Gia Cát Lượng","Vậy để liệu kế khác.","Vậy để liệu kế khác.","Lưu Bị","",},
        [4061] = {7701,"Hạ Hầu Đôn","Mưu kế Gia Cát Lượng, dùng hỏa công tiêu diệt quân ta","Mưu kế Gia Cát Lượng, dùng hỏa công tiêu diệt quân ta","Tào Tháo","",},
        [4062] = {7701,"Tào Tháo","Ngươi dùng binh từ thuở bé, chẳng lẽ không biết rằng ở chốn hiểm phải phòng hoả công à?","Ngươi dùng binh từ thuở bé, chẳng lẽ không biết rằng ở chốn hiểm phải phòng hoả công à?","Hạ Hầu Đôn","",},
        [4063] = {7701,"Hạ Hầu Đôn","Lý Điển, Vu Cấm nói vậy rồi hối không kịp!","Lý Điển, Vu Cấm nói vậy rồi hối không kịp!","Tào Tháo","",},
        [4064] = {7701,"Tào Tháo","Thắng thua là chuyện thường, đừng lo lắng nữa","Thắng thua là chuyện thường, đừng lo lắng nữa","Hạ Hầu Đôn","",},
        [4065] = {7701,"Hạ Hầu Đôn","Lưu Bị hung hăng như thế, thật là cái hoạ lớn không thể không trừ ngay đi.","Lưu Bị hung hăng như thế, thật là cái hoạ lớn không thể không trừ ngay đi.","Tào Tháo","",},
        [4066] = {7701,"Tào Tháo","Ta cũng chỉ lo có Lưu Bị và Tôn Quyền thôi, còn những bọn khác có ngại gì. Nay nên nhân dịp này bình định Giang Nam mới được.","Ta cũng chỉ lo có Lưu Bị và Tôn Quyền thôi, còn những bọn khác có ngại gì. Nay nên nhân dịp này bình định Giang Nam mới được.","Hạ Hầu Đôn","",},
        [4067] = {7701,"Tào Tháo","Huy động 50 vạn quân, sai Tào Nhân và Tào Hồng làm đội thứ nhất","Huy động 50 vạn quân, sai Tào Nhân và Tào Hồng làm đội thứ nhất","Hạ Hầu Đôn","",},
        [4068] = {7701,"Tào Tháo","Trương Liêu và Trương Cáp làm đội thứ nhì; Hạ Hầu Đôn và Hạ Hầu Uyên làm đội thứ ba; Vu Cấm và Lý Điển làm đội thứ tư. Tháo tự lĩnh các tướng làm đội thứ năm. Mỗi đội dẫn mười vạn quân","Trương Liêu và Trương Cáp làm đội thứ nhì; Hạ Hầu Đôn và Hạ Hầu Uyên làm đội thứ ba; Vu Cấm và Lý Điển làm đội thứ tư. Tháo tự lĩnh các tướng làm đội thứ năm. Mỗi đội dẫn mười vạn quân","Hạ Hầu Đôn","",},
        [4069] = {7701,"Tào Tháo","Hứa Chử làm Chiết Xung Tướng, dẫn ba nghìn quân đi tiên phong, lại chọn ngày Bính Ngọ, tháng bảy, năm Kiến An thứ 13 xuất phát.","Hứa Chử làm Chiết Xung Tướng, dẫn ba nghìn quân đi tiên phong, lại chọn ngày Bính Ngọ, tháng bảy, năm Kiến An thứ 13 xuất phát.","Hạ Hầu Đôn","",},
        [4070] = {7702,"Khổng Dung","Lưu Bị, Lưu Biểu dòng dõi hoàng tộc. Tôn Quyền nắm sáu quận, lại được sông Trường Giang hiểm trở, cũng không dễ lấy. Nay thừa tướng huy động đạo quân vô đạo ấy, e trái với nguyện vọng của thiên hạ.","Lưu Bị, Lưu Biểu dòng dõi hoàng tộc. Tôn Quyền nắm sáu quận, lại được sông Trường Giang hiểm trở, cũng không dễ lấy. Nay thừa tướng huy động đạo quân vô đạo ấy, e trái với nguyện vọng của thiên hạ.","Tào Tháo","",},
        [4071] = {7702,"Tào Tháo","Lưu Bị, Lưu Biểu, Tôn Quyền đều là thần tử phản nghịch, phải thảo phạt!","Lưu Bị, Lưu Biểu, Tôn Quyền đều là thần tử phản nghịch, phải thảo phạt!","Khổng Dung","",},
        [4072] = {7702,"Tào Tháo","Lui xuống! Nếu còn tái phạm, trảm ngay lập tức","Lui xuống! Nếu còn tái phạm, trảm ngay lập tức","Khổng Dung","",},
        [4073] = {7703,"Khổng Dung","Hừ!","Hừ!","","",},
        [4074] = {7703,"Khổng Dung","Kẻ bất nhân thảo phạt nhân nghĩa, ắt thất bại!","Kẻ bất nhân thảo phạt nhân nghĩa, ắt thất bại!","","",},
        [4075] = {7704,"Si Lự","Khổng Dung ngày thường vẫn khinh thừa tướng, lại chơi thân với Nễ Hành, Nễ Hành tán dương Khổng Dung là Trọng Mỗ bất tử. Khổng Dung lại gọi Nễ Hành là Nhan Hồi tái sinh.","Khổng Dung ngày thường vẫn khinh thừa tướng, lại chơi thân với Nễ Hành, Nễ Hành tán dương Khổng Dung là Trọng Mỗ bất tử. Khổng Dung lại gọi Nễ Hành là Nhan Hồi tái sinh.","Tào Tháo","",},
        [4076] = {7704,"Si Lự","Lần trước Nễ Hành nói xấu thừa tướng, cũng là tự Khổng Dung xui đó.","Lần trước Nễ Hành nói xấu thừa tướng, cũng là tự Khổng Dung xui đó.","Tào Tháo","",},
        [4077] = {7704,"Si Lự","Hôm qua khi thừa tướng ra khỏi phủ, có nói: Dĩ Chí Bất Nhân Phạt Chí Nhân","Hôm qua khi thừa tướng ra khỏi phủ, có nói: Dĩ Chí Bất Nhân Phạt Chí Nhân","Tào Tháo","",},
        [4078] = {7704,"Tào Tháo","Khổng Dung hay lắm!","Khổng Dung hay lắm!","Si Lự","",},
        [4079] = {7704,"Tào Tháo","Đem Khổng Dung cùng 2 con, xử trảm hết","Đem Khổng Dung cùng 2 con, xử trảm hết","Si Lự","",},
        [4080] = {7801,"Lưu Biểu","Bệnh ta đã nguy kịch, không biết ra đi lúc nào, hiền đệ hãy nuôi nấng con ta. Nhưng con ta bất tài, sợ không nối được nghiệp bố. Sau khi ta đi, xin hiền đệ hãy cai quản Kinh Châu","Bệnh ta đã nguy kịch, không biết ra đi lúc nào, hiền đệ hãy nuôi nấng con ta. Nhưng con ta bất tài, sợ không nối được nghiệp bố. Sau khi ta đi, xin hiền đệ hãy cai quản Kinh Châu","Lưu Bị","",},
        [4081] = {7801,"Lưu Bị","Ta sẽ giúp đỡ hết sức, đâu dám có ý gì khác.","Ta sẽ giúp đỡ hết sức, đâu dám có ý gì khác.","Lưu Biểu","",},
        [4082] = {7801,"Lưu Bị","Tào Tháo tự thống lĩnh đại binh đến đánh. Huyền Đức vội vàng về lại Tân Dã.","Tào Tháo tự thống lĩnh đại binh đến đánh. Huyền Đức vội vàng về lại Tân Dã.","Lưu Biểu","",},
        [4083] = {7802,"Lưu Kỳ","Ta ở tại Giang Hạ, biết tin phụ thân ốm, liền đến thăm","Ta ở tại Giang Hạ, biết tin phụ thân ốm, liền đến thăm","Thái Mạo","",},
        [4084] = {7802,"Thái Mạo","Công tử phụng mệnh trấn giữ Giang Hạ, trách nhiệm rất nặng, nay dám tự tiện bỏ về, Đông Ngô kéo đến, thì làm thế nào?","Công tử phụng mệnh trấn giữ Giang Hạ, trách nhiệm rất nặng, nay dám tự tiện bỏ về, Đông Ngô kéo đến, thì làm thế nào?","Lưu Kỳ","",},
        [4085] = {7802,"Thái Mạo","Nếu vào gặp chúa công, chúa công nổi giận, bệnh lại nặng thêm, đó là bất hiếu. Công tử nên cấp tốc về ngay đi!","Nếu vào gặp chúa công, chúa công nổi giận, bệnh lại nặng thêm, đó là bất hiếu. Công tử nên cấp tốc về ngay đi!","Lưu Kỳ","",},
        [4086] = {7802,"Lưu Kỳ","Cái này...","Cái này...","Thái Mạo","",},
        [4087] = {7802,"Lưu Kỳ","Xin hãy chăm sóc chúa công, nếu có biến hãy báo tin cho ta biết!","Xin hãy chăm sóc chúa công, nếu có biến hãy báo tin cho ta biết!","Thái Mạo","",},
        [4088] = {7803,"Thái Phu Nhân","Lưu Biểu đã mất, hiện giờ phải làm gì?","Lưu Biểu đã mất, hiện giờ phải làm gì?","Thái Mạo","",},
        [4089] = {7803,"Thái Mạo","Làm giả di chúc, để con trai thứ 2 là Lưu Tông làm chủ Kinh Châu.","Làm giả di chúc, để con trai thứ 2 là Lưu Tông làm chủ Kinh Châu.","Thái Phu Nhân","",},
        [4090] = {7803,"Thái Phu Nhân","Được!","Được!","Thái Mạo","",},
        [4091] = {7804,"Lưu Tông","Cha ta tạ thế, anh ta hiện ở Giang Hạ, chú ta là Huyền Đức ở Tân Dã. Các ngươi lập ta làm chủ, nếu chú ta và anh ta về hỏi tội, thì ăn nói làm sao?","Cha ta tạ thế, anh ta hiện ở Giang Hạ, chú ta là Huyền Đức ở Tân Dã. Các ngươi lập ta làm chủ, nếu chú ta và anh ta về hỏi tội, thì ăn nói làm sao?","","",},
        [4092] = {7804,"Lý Khuê","Công tử nói rất phải. Nay nên đưa thư cáo tang đến Giang Hạ, mời đại công tử về làm chủ Kinh Châu rồi cử Huyền Đức cùng coi việc lớn, đó là kế sách hoàn hảo","Công tử nói rất phải. Nay nên đưa thư cáo tang đến Giang Hạ, mời đại công tử về làm chủ Kinh Châu rồi cử Huyền Đức cùng coi việc lớn, đó là kế sách hoàn hảo","Lưu Tông","",},
        [4093] = {7804,"Lý Khuê","Bắc chống Tào Tháo, Nam chống Tôn Quyền. Đây là kế hoạch hay nhất.","Bắc chống Tào Tháo, Nam chống Tôn Quyền. Đây là kế hoạch hay nhất.","Lưu Tông","",},
        [4094] = {7804,"Thái Mạo","Ngươi là ai, sao dám nói điều trái lời chúa công!","Ngươi là ai, sao dám nói điều trái lời chúa công!","Lý Khuê","",},
        [4095] = {7804,"Lý Khuê","Ngươi nội ứng ngoại hợp, giả truyền di mệnh, phế trưởng lập ấu, Kinh Tương chín quận dâng cả cho Thái Thị! Cố chủ có linh thiêng sẽ không tha cho ngươi!","Ngươi nội ứng ngoại hợp, giả truyền di mệnh, phế trưởng lập ấu, Kinh Tương chín quận dâng cả cho Thái Thị! Cố chủ có linh thiêng sẽ không tha cho ngươi!","Thái Mạo","",},
        [4096] = {7804,"Thái Mạo","Lôi ra ngoài xử trảm!","Lôi ra ngoài xử trảm!","Lý Khuê","",},
        [4097] = {7805,"Lưu Tông","Tào Tháo dẫn quân đến rồi, phải làm sao đây","Tào Tháo dẫn quân đến rồi, phải làm sao đây","Vương Sán","",},
        [4098] = {7805,"Vương Sán","Tướng quân chuẩn bị so với Tào Công như thế nào?","Tướng quân chuẩn bị so với Tào Công như thế nào?","Lưu Tông","",},
        [4099] = {7805,"Lưu Tông","Không bằng","Không bằng","Vương Sán","",},
        [4100] = {7805,"Vương Sán","Tào công binh cường tướng khoẻ, đa mưu túc trí, bắt sống Lữ Bố ở Hạ Phì, phá vỡ Quan Thiệu ở Quan Độ, đuổi Lưu Bị ở Lũng Hữu, trừ Ô Hoàn ở Bạch Lang, chiến công hiển hách","Tào công binh cường tướng khoẻ, đa mưu túc trí, bắt sống Lữ Bố ở Hạ Phì, phá vỡ Quan Thiệu ở Quan Độ, đuổi Lưu Bị ở Lũng Hữu, trừ Ô Hoàn ở Bạch Lang, chiến công hiển hách","Lưu Tông","",},
        [4101] = {7805,"Vương Sán","Nay lại kéo đại quân xuống Nam Hạ, thế Kinh Tương thực khó lòng địch nổi. Kế của hai ông Phó, Khoái rất hay. Tướng quân nên quyết ngay đi, kẻo về sau lại hối hân.","Nay lại kéo đại quân xuống Nam Hạ, thế Kinh Tương thực khó lòng địch nổi. Kế của hai ông Phó, Khoái rất hay. Tướng quân nên quyết ngay đi, kẻo về sau lại hối hân.","Lưu Tông","",},
        [4102] = {7805,"Lưu Tông","Ngài dạy rất phải, vậy mẫu thân thấy thế nào?","Ngài dạy rất phải, vậy mẫu thân thấy thế nào?","Vương Sán","",},
        [4103] = {7805,"Thái Phu Nhân","Ta đồng ý đầu hàng","Ta đồng ý đầu hàng","Lưu Tông","",},
        [4104] = {7805,"Lưu Tông","Vậy ta sẽ dâng thư xin hàng","Vậy ta sẽ dâng thư xin hàng","Thái Phu Nhân","",},
        [4105] = {7806,"Lưu Bị","Lưu Kỳ trấn thủ Giang Hạ, Thái Phu Nhân cùng Thái Mạo viết di chúc giả cho Lưu Tôn làm chủ Kinh Châu, xong rồi mới cho báo tang.","Lưu Kỳ trấn thủ Giang Hạ, Thái Phu Nhân cùng Thái Mạo viết di chúc giả cho Lưu Tôn làm chủ Kinh Châu, xong rồi mới cho báo tang.","","",},
        [4106] = {7806,"Lưu Bị","Ngài chưa biết Lưu Tôn đã đem cả chín quận Kinh Tương dâng lên Tào Tháo rồi!","Ngài chưa biết Lưu Tôn đã đem cả chín quận Kinh Tương dâng lên Tào Tháo rồi!","","",},
        [4107] = {7806,"Lưu Bị","Lúc anh ta sắp mất đã gửi con cho ta. Nếu ta bắt lấy con, cướp lấy đất, thì sau này xuống chín suối còn mặt mũi nào trông thấy anh ta nữa!","Lúc anh ta sắp mất đã gửi con cho ta. Nếu ta bắt lấy con, cướp lấy đất, thì sau này xuống chín suối còn mặt mũi nào trông thấy anh ta nữa!","","",},
        [4108] = {7806,"Nhân vật","Đáng ghét, ta sẽ đi giáo huấn họ","Đáng ghét, ta sẽ đi giáo huấn họ","Lưu Bị","",},
