        [4119] = {7902,"Gia Cát Lượng","Cuối canh ba hôm sau, hễ nghe tiếng người ngựa ở hạ lưu thì vớt những túi cát lên cho nước tràn xuống, thuận theo dòng nước đến tiếp ứng.","Cuối canh ba hôm sau, hễ nghe tiếng người ngựa ở hạ lưu thì vớt những túi cát lên cho nước tràn xuống, thuận theo dòng nước đến tiếp ứng.","Lưu Bị","",},
        [4120] = {7902,"Gia Cát Lượng","Trương Phi dẫn 1000 quân mai phục ở bến đò Bác Lăng. Khúc sông này nước chảy chậm, quân Tào bị ngập sẽ trốn qua lối đó, bấy giờ thừa thế tiếp ứng.","Trương Phi dẫn 1000 quân mai phục ở bến đò Bác Lăng. Khúc sông này nước chảy chậm, quân Tào bị ngập sẽ trốn qua lối đó, bấy giờ thừa thế tiếp ứng.","Lưu Bị","",},
        [4121] = {7902,"Gia Cát Lượng","Triệu Vân dẫn 3000 quân, chia làm 4 đội. Vân tự lĩnh 1 đội quân mai phục phía ngoài cổng phía đông. 3 đội còn lại phân chia mai phục các hướng. Những ngôi nhà trong thành, gài những vật bắt lửa","Triệu Vân dẫn 3000 quân, chia làm 4 đội. Vân tự lĩnh 1 đội quân mai phục phía ngoài cổng phía đông. 3 đội còn lại phân chia mai phục các hướng. Những ngôi nhà trong thành, gài những vật bắt lửa","Lưu Bị","",},
        [4122] = {7902,"Gia Cát Lượng","Quân Tào vào thành tất phải nghỉ ở nhà dân. Chiều tối hôm sau thế nào cũng có gió lớn. Hễ nổi gió,quân phục ở 3 cửa tây, nam, bắc bắn tên lửa vào thành","Quân Tào vào thành tất phải nghỉ ở nhà dân. Chiều tối hôm sau thế nào cũng có gió lớn. Hễ nổi gió,quân phục ở 3 cửa tây, nam, bắc bắn tên lửa vào thành","Lưu Bị","",},
        [4123] = {7902,"Gia Cát Lượng","Lúc lửa bốc lên, bên ngoài hò reo trợ oai. Cửa đông bỏ ngỏ cho giặc chạy. Khi giặc chạy ra, thừa thế đuổi đánh","Lúc lửa bốc lên, bên ngoài hò reo trợ oai. Cửa đông bỏ ngỏ cho giặc chạy. Khi giặc chạy ra, thừa thế đuổi đánh","Lưu Bị","",},
        [4124] = {7902,"Gia Cát Lượng","Trời sáng, Quan Vũ Trương Phi thu quân về Phàn Thành. Mi Phương, Lưu Phong dẫn dắt 2000 quân, một nửa cờ đỏ, một nửa cờ xanh đi đến ngoại thành Tân Dã lập đồn trú.","Trời sáng, Quan Vũ Trương Phi thu quân về Phàn Thành. Mi Phương, Lưu Phong dẫn dắt 2000 quân, một nửa cờ đỏ, một nửa cờ xanh đi đến ngoại thành Tân Dã lập đồn trú.","Lưu Bị","",},
        [4125] = {7902,"Gia Cát Lượng","Nhìn thấy quân Tào đến, quân cờ đỏ bên trái, quân cờ xanh bên phải, hắn hoài nghi ắt sẽ không dám đuổi theo. Hai người họ sẽ đến mai phục.","Nhìn thấy quân Tào đến, quân cờ đỏ bên trái, quân cờ xanh bên phải, hắn hoài nghi ắt sẽ không dám đuổi theo. Hai người họ sẽ đến mai phục.","Lưu Bị","",},
