        [4329] = {8307,"Tôn Quyền","Ta cũng chưa quyết định.","Ta cũng chưa quyết định.","Lỗ Túc","",},
        [4330] = {8307,"Trương Chiêu","Tào Tháo thống lĩnh quân trăm vạn, mượn tiếng thiên tử, chinh chiến bốn phương, mình chống cự lại thì là nghịch.","Tào Tháo thống lĩnh quân trăm vạn, mượn tiếng thiên tử, chinh chiến bốn phương, mình chống cự lại thì là nghịch.","Tôn Quyền","",},
        [4331] = {8307,"Trương Chiêu","Nay Tháo chiếm được Kinh Châu, ta khó địch được nữa. Vậy cứ ý ta, đầu hàng là kế vẹn toàn.","Nay Tháo chiếm được Kinh Châu, ta khó địch được nữa. Vậy cứ ý ta, đầu hàng là kế vẹn toàn.","Tôn Quyền","",},
        [4332] = {8307,"Tôn Quyền","Cái này...","Cái này...","Trương Chiêu","",},
        [4333] = {8307,"Trương Chiêu","Chúa công không nên nghi ngại. Nếu hàng Tào thì dân Đông Ngô được yên, mà sáu quận Giang Nam cũng giữ được.","Chúa công không nên nghi ngại. Nếu hàng Tào thì dân Đông Ngô được yên, mà sáu quận Giang Nam cũng giữ được.","Tôn Quyền","",},
        [4334] = {8307,"Tôn Quyền","Cái này...","Cái này...","Trương Chiêu","",},
        [4335] = {8308,"Lỗ Túc","Ai cũng có thể hàng Tào Tháo được, duy có tướng quân thì không hàng được.","Ai cũng có thể hàng Tào Tháo được, duy có tướng quân thì không hàng được.","Tôn Quyền","",},
        [4336] = {8308,"Tôn Quyền","Vì sao?","Vì sao?","Lỗ Túc","",},
        [4337] = {8308,"Lỗ Túc","Như Lỗ Túc này mà hàng, áo gấm về làng, mà cũng không phải mất đất đai gì cả.","Như Lỗ Túc này mà hàng, áo gấm về làng, mà cũng không phải mất đất đai gì cả.","Tôn Quyền","",},
        [4338] = {8308,"Lỗ Túc","Tướng quân mà hàng Tào thì về đâu? Chức tước bất quá phong hầu là cùng, xe một cỗ, ngựa một con, đầy tớ vài ba người.","Tướng quân mà hàng Tào thì về đâu? Chức tước bất quá phong hầu là cùng, xe một cỗ, ngựa một con, đầy tớ vài ba người.","Tôn Quyền","",},
        [4339] = {8308,"Lỗ Túc","Họ ai cũng chỉ biết thân người nấy mà thôi, xin tướng quân chớ nghe, nên sớm định kế lớn.","Họ ai cũng chỉ biết thân người nấy mà thôi, xin tướng quân chớ nghe, nên sớm định kế lớn.","Tôn Quyền","",},
        [4340] = {8308,"Tôn Quyền","Ta nghe họ nghị luận cảm thấy thất vọng. Tử Kính mới ngỏ kế lớn, chính hợp ý với ta.","Ta nghe họ nghị luận cảm thấy thất vọng. Tử Kính mới ngỏ kế lớn, chính hợp ý với ta.","Lỗ Túc","",},
        [4341] = {8308,"Tôn Quyền","Quả thật trời ban Tử Kính cho ta! Nhưng Tháo mới được quân Viên Thiệu và Kinh Châu, thế mạnh khó mà địch nổi.","Quả thật trời ban Tử Kính cho ta! Nhưng Tháo mới được quân Viên Thiệu và Kinh Châu, thế mạnh khó mà địch nổi.","Lỗ Túc","",},
        [4342] = {8308,"Lỗ Túc","Túc vừa sang Giang Hạ, đưa Gia Cát Lượng về đây, chúa công gọi vào hỏi sẽ biết tình hình.","Túc vừa sang Giang Hạ, đưa Gia Cát Lượng về đây, chúa công gọi vào hỏi sẽ biết tình hình.","Tôn Quyền","",},
        [4343] = {8308,"Tôn Quyền","Ngọa Long tiên sinh có ở đây?","Ngọa Long tiên sinh có ở đây?","Lỗ Túc","",},
        [4344] = {8308,"Lỗ Túc","Hiện đang nghỉ ở ngoài nhà khách.","Hiện đang nghỉ ở ngoài nhà khách.","Tôn Quyền","",},
        [4345] = {8308,"Tôn Quyền","Nay trời đã tối, không gặp vội. Ngày mai cho gặp các tay anh tuấn Giang Đông đã, rồi sẽ mời lên triều đường bàn việc.","Nay trời đã tối, không gặp vội. Ngày mai cho gặp các tay anh tuấn Giang Đông đã, rồi sẽ mời lên triều đường bàn việc.","Lỗ Túc","",},
        [4346] = {8309,"Trương Chiêu","Chiêu ta nghe danh tiên sinh ví mình với Quản Trọng, Nhạc Nghị, lời ấy chẳng biết thực hư?","Chiêu ta nghe danh tiên sinh ví mình với Quản Trọng, Nhạc Nghị, lời ấy chẳng biết thực hư?","Gia Cát Lượng","",},
        [4347] = {8309,"Gia Cát Lượng","Lượng ta cũng ví mình như thế","Lượng ta cũng ví mình như thế","Trương Chiêu","",},
        [4348] = {8309,"Trương Chiêu","Mới đây nghe Lưu Dự Châu ba lần cầu đến tiên sinh, càn quét Kinh Tương, nay chỉ có một buổi sáng mà về tay Tào Tháo hết, chẳng hay ông thế nào?","Mới đây nghe Lưu Dự Châu ba lần cầu đến tiên sinh, càn quét Kinh Tương, nay chỉ có một buổi sáng mà về tay Tào Tháo hết, chẳng hay ông thế nào?","Gia Cát Lượng","",},
        [4349] = {8309,"Gia Cát Lượng","Chủ ta là Lưu Dự Châu, vì không nỡ cướp cơ nghiệp của người đồng tông nên nhất định không lấy.","Chủ ta là Lưu Dự Châu, vì không nỡ cướp cơ nghiệp của người đồng tông nên nhất định không lấy.","Trương Chiêu","",},
        [4350] = {8309,"Gia Cát Lượng","Chủ ta đóng quân ở Giang Hạ, sẽ có kế khác, những kẻ tầm thường có hiểu sao được?","Chủ ta đóng quân ở Giang Hạ, sẽ có kế khác, những kẻ tầm thường có hiểu sao được?","Trương Chiêu","",},
        [4351] = {8309,"Trương Chiêu","Nếu thế thì tiên sinh lời nói không đi đôi với việc làm rồi. ","Nếu thế thì tiên sinh lời nói không đi đôi với việc làm rồi. ","Gia Cát Lượng","",},
        [4352] = {8309,"Trương Chiêu","Ai ngờ khi tiên sinh về với Dự Châu, quân Tào mới đến, bỏ giáp quẳng gươm.","Ai ngờ khi tiên sinh về với Dự Châu, quân Tào mới đến, bỏ giáp quẳng gươm.","Gia Cát Lượng","",},
        [4353] = {8309,"Trương Chiêu","Bỏ Tân Dã, chạy khỏi Phàn Thành, chẳng có lấy một chỗ dung thân. Hóa ra Dự Châu từ khi được tiên sinh lại không bằng trước?","Bỏ Tân Dã, chạy khỏi Phàn Thành, chẳng có lấy một chỗ dung thân. Hóa ra Dự Châu từ khi được tiên sinh lại không bằng trước?","Gia Cát Lượng","",},
        [4354] = {8309,"Gia Cát Lượng","Chủ ta, trước thua ở Nhữ Ân, đến nhờ Lưu Biểu, quân không đầy một nghìn, Dự Châu chỉ là chốn  tạm, có phải muốn khư khư ngồi giữ cái xó ấy đâu?","Chủ ta, trước thua ở Nhữ Ân, đến nhờ Lưu Biểu, quân không đầy một nghìn, Dự Châu chỉ là chốn  tạm, có phải muốn khư khư ngồi giữ cái xó ấy đâu?","Trương Chiêu","",},
        [4355] = {8309,"Gia Cát Lượng","Trong hoàn cảnh quân sĩ không sẵn, thành quách không bền, khiến Hạ Hầu Đôn, Tào Nhân phải lòng run mật vỡ.","Trong hoàn cảnh quân sĩ không sẵn, thành quách không bền, khiến Hạ Hầu Đôn, Tào Nhân phải lòng run mật vỡ.","Trương Chiêu","",},
        [4356] = {8309,"Gia Cát Lượng","Lưu Tôn hàng Tào, Dự Châu hoàn toàn không biết. Vả lại, không nỡ nhân lúc loạn mà cướp lấy cơ nghiệp người đồng tông.","Lưu Tôn hàng Tào, Dự Châu hoàn toàn không biết. Vả lại, không nỡ nhân lúc loạn mà cướp lấy cơ nghiệp người đồng tông.","Trương Chiêu","",},
        [4357] = {8309,"Gia Cát Lượng","Trận thua ở Đương Dương, vì có vài vạn dân như gồm già trẻ, nên mỗi ngày chỉ đi được mười dặm.","Trận thua ở Đương Dương, vì có vài vạn dân như gồm già trẻ, nên mỗi ngày chỉ đi được mười dặm.","Trương Chiêu","",},
        [4358] = {8309,"Trương Chiêu","Cái này...","Cái này...","Gia Cát Lượng","",},
        [4359] = {8310,"Tôn Quyền","Tiên sinh ở Tân Dã, giúp Lưu Dự Châu giao chiến với Tào Tháo, tất biết tình hình bên Tào thế nào?","Tiên sinh ở Tân Dã, giúp Lưu Dự Châu giao chiến với Tào Tháo, tất biết tình hình bên Tào thế nào?","Gia Cát Lượng","",},
        [4360] = {8310,"Gia Cát Lượng","Cả thiết kỵ, bộ binh và thủy quân ước được hơn trăm vạn.","Cả thiết kỵ, bộ binh và thủy quân ước được hơn trăm vạn.","Tôn Quyền","",},
        [4361] = {8310,"Tôn Quyền","Nay Tào Tháo đã bình được Kinh Sở, còn tham vọng gì nữa không?","Nay Tào Tháo đã bình được Kinh Sở, còn tham vọng gì nữa không?","Gia Cát Lượng","",},
        [4362] = {8310,"Gia Cát Lượng","Tào Tháo xây trại kín ven sông, sắm sửa thuyền bè, không lấy Giang Đông thì lấy đâu?","Tào Tháo xây trại kín ven sông, sắm sửa thuyền bè, không lấy Giang Đông thì lấy đâu?","Tôn Quyền","",},
        [4363] = {8310,"Tôn Quyền","Đối với chuyện này, nên đánh hay không, xin giúp ta quyết định.","Đối với chuyện này, nên đánh hay không, xin giúp ta quyết định.","Gia Cát Lượng","",},
        [4364] = {8310,"Gia Cát Lượng","Trước kia thiên hạ đại loạn, nên tướng quân dấy binh ở Giang Đông, Lưu Dự Châu tụ quân ở Hán Nam, cùng giành với Tào Tháo.","Trước kia thiên hạ đại loạn, nên tướng quân dấy binh ở Giang Đông, Lưu Dự Châu tụ quân ở Hán Nam, cùng giành với Tào Tháo.","Tôn Quyền","",},
        [4365] = {8310,"Gia Cát Lượng","Nay Tháo đã dẹp được nạn lớn, phá được Kinh Châu, uy lừng bốn bể.","Nay Tháo đã dẹp được nạn lớn, phá được Kinh Châu, uy lừng bốn bể.","Tôn Quyền","",},
        [4366] = {8310,"Gia Cát Lượng","Nếu tướng quân có thể lấy quân Ngô chống lại Tào Tháo, thì nên sớm cự tuyệt. Nếu không muốn thế, sao không theo lời các mưu sĩ, ngoảnh về phương bắc mà hàng?","Nếu tướng quân có thể lấy quân Ngô chống lại Tào Tháo, thì nên sớm cự tuyệt. Nếu không muốn thế, sao không theo lời các mưu sĩ, ngoảnh về phương bắc mà hàng?","Tôn Quyền","",},
        [4367] = {8310,"Gia Cát Lượng","Tướng quân trong lòng vẫn còn ngờ vực. Việc gấp mà không quyết đoán, thì vạ đến nơi!","Tướng quân trong lòng vẫn còn ngờ vực. Việc gấp mà không quyết đoán, thì vạ đến nơi!","Tôn Quyền","",},
        [4368] = {8310,"Tôn Quyền","Nếu như vậy thật, thì sao Lưu Dự Châu không hàng Tào?","Nếu như vậy thật, thì sao Lưu Dự Châu không hàng Tào?","Gia Cát Lượng","",},
        [4369] = {8310,"Gia Cát Lượng","Lưu Dự Châu là tôn thân nhà Hán, anh tài cái thế. Việc không xong là bởi trời, có đâu lại chịu luồn cúi người ta?","Lưu Dự Châu là tôn thân nhà Hán, anh tài cái thế. Việc không xong là bởi trời, có đâu lại chịu luồn cúi người ta?","Tôn Quyền","",},
        [4370] = {8310,"Lỗ Túc","To gan, những lời tiên sinh vừa nói là khinh miệt chủ ta. Sau này bàn tiếp.","To gan, những lời tiên sinh vừa nói là khinh miệt chủ ta. Sau này bàn tiếp.","Gia Cát Lượng","",},
        [4371] = {8311,"Tôn Quyền","Tào Tháo vốn chỉ ghét Lữ Bố, Lưu Biểu... Nay đã trừ được cả, duy chỉ còn Dự Châu và ta. Ta không thể đem cả nước Ngô dâng cho người ta được","Tào Tháo vốn chỉ ghét Lữ Bố, Lưu Biểu... Nay đã trừ được cả, duy chỉ còn Dự Châu và ta. Ta không thể đem cả nước Ngô dâng cho người ta được","Gia Cát Lượng","",},
        [4372] = {8311,"Tôn Quyền","Kế của ta đã quyết. Dự Châu vừa mới thua trận, làm thế nào mà chống được nạn này.","Kế của ta đã quyết. Dự Châu vừa mới thua trận, làm thế nào mà chống được nạn này.","Gia Cát Lượng","",},
        [4373] = {8311,"Gia Cát Lượng","Dự Châu tuy mới thua, nhưng Quan Vân Trường còn thống lĩnh hàng vạn tinh binh,.","Dự Châu tuy mới thua, nhưng Quan Vân Trường còn thống lĩnh hàng vạn tinh binh,.","Tôn Quyền","",},
        [4374] = {8311,"Gia Cát Lượng","Quân Tào Tháo từ xa đến, tất cũng mỏi mệt, vả lại người phương bắc không quen chiến đấu trên nước.","Quân Tào Tháo từ xa đến, tất cũng mỏi mệt, vả lại người phương bắc không quen chiến đấu trên nước.","Tôn Quyền","",},
        [4375] = {8311,"Gia Cát Lượng","Quân dân ở Kinh Châu gặp thế bí phải phục Tào, chứ không phải tự nguyện.","Quân dân ở Kinh Châu gặp thế bí phải phục Tào, chứ không phải tự nguyện.","Tôn Quyền","",},
        [4376] = {8311,"Gia Cát Lượng","Nay tướng quân muốn đoàn kết với Dự Châu, chắc chắn phá được Tào Tháo.","Nay tướng quân muốn đoàn kết với Dự Châu, chắc chắn phá được Tào Tháo.","Tôn Quyền","",},
        [4377] = {8311,"Gia Cát Lượng","Thắng thua chỉ trong lúc này, tướng quân nghĩ kỹ rồi quyết định!","Thắng thua chỉ trong lúc này, tướng quân nghĩ kỹ rồi quyết định!","Tôn Quyền","",},
        [4378] = {8311,"Tôn Quyền","Ta đã quyết định, hôm sau khởi binh cùng diệt Tào Tháo!","Ta đã quyết định, hôm sau khởi binh cùng diệt Tào Tháo!","Gia Cát Lượng","",},
        [4379] = {8312,"Trương Chiêu","Trúng kế của Khổng Minh rồi!","Trúng kế của Khổng Minh rồi!","Tôn Quyền","",},
        [4380] = {8312,"Trương Chiêu","Tào Tháo khi trước binh mọn, tướng ít, còn đánh được Viên Thiệu; huống bây giờ cầm hơn trăm vạn quân, há nên khinh địch?","Tào Tháo khi trước binh mọn, tướng ít, còn đánh được Viên Thiệu; huống bây giờ cầm hơn trăm vạn quân, há nên khinh địch?","Tôn Quyền","",},
        [4381] = {8312,"Trương Chiêu","Nếu nghe lời Gia Cát Lượng, hấp tấp dấy binh, là đem củi khô đi chữa cháy.","Nếu nghe lời Gia Cát Lượng, hấp tấp dấy binh, là đem củi khô đi chữa cháy.","Tôn Quyền","",},
        [4382] = {8312,"Tôn Quyền","Cái này...","Cái này...","Trương Chiêu","",},
        [4383] = {8312,"Cố Ung","Lưu Bị thua vì Tào Tháo, nên muốn mượn quân ta để cự lại Tháo. Hãy nghe lời Tử Bố.","Lưu Bị thua vì Tào Tháo, nên muốn mượn quân ta để cự lại Tháo. Hãy nghe lời Tử Bố.","Tôn Quyền","",},
        [4384] = {8312,"Tôn Quyền","Cái này...","Cái này...","Cố Ung","",},
        [4385] = {8312,"Lỗ Túc","Bọn Trương Tử Bố khuyên tướng quân hàng Tào Tháo chỉ biết giữ mình và vợ con, chúa công đừng nghe.","Bọn Trương Tử Bố khuyên tướng quân hàng Tào Tháo chỉ biết giữ mình và vợ con, chúa công đừng nghe.","Tôn Quyền","",},
        [4386] = {8312,"Lỗ Túc","Nếu chúa công hoài nghi, tất bị chúng làm lỡ việc.","Nếu chúa công hoài nghi, tất bị chúng làm lỡ việc.","Tôn Quyền","",},
        [4387] = {8312,"Tôn Quyền","Ngươi lui ra đã, để ta suy nghĩ.","Ngươi lui ra đã, để ta suy nghĩ.","Lỗ Túc","",},
        [4388] = {8313,"Ngô Quốc Thái","Suy nghĩ gì mà bỏ ăn bỏ ngủ?","Suy nghĩ gì mà bỏ ăn bỏ ngủ?","Tôn Quyền","",},
        [4389] = {8313,"Tôn Quyền","Tào Tháo đóng quân ở Giang Hán. Nếu đánh thì sợ mình ít không địch nổi nhiều, muốn hàng thì sợ Tào Tháo không dung.","Tào Tháo đóng quân ở Giang Hán. Nếu đánh thì sợ mình ít không địch nổi nhiều, muốn hàng thì sợ Tào Tháo không dung.","Ngô Quốc Thái","",},
        [4390] = {8313,"Ngô Quốc Thái","Ngươi không nhớ lời Võ Liệt Hoàng Hậu lúc lâm chung dặn?","Ngươi không nhớ lời Võ Liệt Hoàng Hậu lúc lâm chung dặn?","Tôn Quyền","",},
        [4391] = {8313,"Ngô Quốc Thái","Chuyện trong hỏi Trương Chiêu, chuyện ngoài hỏi Chu Du.","Chuyện trong hỏi Trương Chiêu, chuyện ngoài hỏi Chu Du.","Tôn Quyền","",},
        [4392] = {8313,"Ngô Quốc Thái","Sao con mời Chu Du về hỏi?","Sao con mời Chu Du về hỏi?","Tôn Quyền","",},
        [4393] = {8401,"Trương Chiêu","Đô đốc có biết việc mất còn của Giang Đông không?","Đô đốc có biết việc mất còn của Giang Đông không?","Chu Du","",},
        [4394] = {8401,"Chu Du","Chưa biết.","Chưa biết.","Trương Chiêu","",},
        [4395] = {8401,"Trương Chiêu","Tào Tháo cầm trăm vạn quân, đóng ở Hán Thượng. Tuy hắn định thôn tính ta, nhưng chưa nói rõ ra thôi.","Tào Tháo cầm trăm vạn quân, đóng ở Hán Thượng. Tuy hắn định thôn tính ta, nhưng chưa nói rõ ra thôi.","Chu Du","",},
        [4396] = {8401,"Trương Chiêu","Bọn Chiêu đã khuyên chúa công nên hàng, họa may Giang Đông mới tránh được vạ. Không ngờ Gia Cát Lượng vì muốn trả thù Tào Tháo, nên nói khích chúa công.","Bọn Chiêu đã khuyên chúa công nên hàng, họa may Giang Đông mới tránh được vạ. Không ngờ Gia Cát Lượng vì muốn trả thù Tào Tháo, nên nói khích chúa công.","Chu Du","",},
        [4397] = {8401,"Trương Chiêu","Tử Kính cố chấp, chỉ đợi đô đốc về quyết định dứt khoát.","Tử Kính cố chấp, chỉ đợi đô đốc về quyết định dứt khoát.","Chu Du","",},
        [4398] = {8401,"Chu Du","Người có nghĩ như vậy không?","Người có nghĩ như vậy không?","Trương Chiêu","",},
        [4399] = {8401,"Cố Ung","Ý kiến giống nhau.","Ý kiến giống nhau.","Chu Du","",},
        [4400] = {8401,"Chu Du","Ta cũng muốn hàng đã lâu. Sáng mai gặp chúa công, ắt sẽ có quyết định.","Ta cũng muốn hàng đã lâu. Sáng mai gặp chúa công, ắt sẽ có quyết định.","Cố Ung","",},
        [4401] = {8402,"Trình Phổ","Đô đốc có biết Giang Đông nay mai về tay người khác không?","Đô đốc có biết Giang Đông nay mai về tay người khác không?","Chu Du","",},
        [4402] = {8402,"Chu Du","Chưa biết.","Chưa biết.","Trình Phổ","",},
        [4403] = {8402,"Trình Phổ","Bọn ta theo Tôn tướng quân mở mang cơ nghiệp, hơn trăm trận đánh mới có được sáu quận. Nay chúa công muốn hàng Tào Tháo, quả thật hổ thẹn! ","Bọn ta theo Tôn tướng quân mở mang cơ nghiệp, hơn trăm trận đánh mới có được sáu quận. Nay chúa công muốn hàng Tào Tháo, quả thật hổ thẹn! ","Chu Du","",},
        [4404] = {8402,"Trình Phổ","Xin đô đốc khuyên chúa công quyết kế khởi binh, bọn ta nguyện chiến đấu đến cùng.","Xin đô đốc khuyên chúa công quyết kế khởi binh, bọn ta nguyện chiến đấu đến cùng.","Chu Du","",},
        [4405] = {8402,"Chu Du","Ý kiến các tướng quân giống nhau không?","Ý kiến các tướng quân giống nhau không?","Trình Phổ","",},
        [4406] = {8402,"Hoàng Cái","Ta thà hy sinh, chứ không hàng Tào!","Ta thà hy sinh, chứ không hàng Tào!","Chu Du","",},
        [4407] = {8402,"Chu Du","Xin các tướng hãy về, ta vào gặp chúa công sẽ có quyết định.","Xin các tướng hãy về, ta vào gặp chúa công sẽ có quyết định.","Hoàng Cái","",},
        [4408] = {8403,"Gia Cát Cẩn","Gia Cát Lượng nói Lưu Dự Châu muốn kết với Đông Ngô để đánh Tào Tháo. Các quan văn võ bàn định chưa xong, chỉ đợi đô đốc về quyết định.","Gia Cát Lượng nói Lưu Dự Châu muốn kết với Đông Ngô để đánh Tào Tháo. Các quan văn võ bàn định chưa xong, chỉ đợi đô đốc về quyết định.","Chu Du","",},
        [4409] = {8403,"Chu Du","Ý ông thế nào?","Ý ông thế nào?","Gia Cát Cẩn","",},
        [4410] = {8403,"Gia Cát Cẩn","Hàng thì dễ yên, đánh thì khó giữ.","Hàng thì dễ yên, đánh thì khó giữ.","Chu Du","",},
        [4411] = {8403,"Chu Du","Ta đã có chủ trương, ngày mai đến phủ sẽ quyết định.","Ta đã có chủ trương, ngày mai đến phủ sẽ quyết định.","Gia Cát Cẩn","",},
        [4412] = {8404,"Lỗ Túc","Nay Tào Tháo đánh chiếm phía Nam, hoà với đánh, chỉ có hai đường. Ý kiến tướng quân thế nào?","Nay Tào Tháo đánh chiếm phía Nam, hoà với đánh, chỉ có hai đường. Ý kiến tướng quân thế nào?","Chu Du","",},
        [4413] = {8404,"Chu Du","Tào Tháo mượn tiếng thiên tử, thì không nên kháng cự. Đánh thì tất thua, hàng thì dễ yên. Ngày mai ta vào hầu chúa công xin sai sứ đi hàng Tào.","Tào Tháo mượn tiếng thiên tử, thì không nên kháng cự. Đánh thì tất thua, hàng thì dễ yên. Ngày mai ta vào hầu chúa công xin sai sứ đi hàng Tào.","Lỗ Túc","",},
        [4414] = {8404,"Lỗ Túc","Ông nói lầm rồi! Cơ nghiệp Giang Đông đã trải ba đời, sao một chốc mà để vào tay người khác?","Ông nói lầm rồi! Cơ nghiệp Giang Đông đã trải ba đời, sao một chốc mà để vào tay người khác?","Chu Du","",},
        [4415] = {8404,"Lỗ Túc","Đang là lúc trông cậy tướng quân giữ cơ nghiệp, không ngờ ông lại theo lời những kẻ hèn nhát đó?","Đang là lúc trông cậy tướng quân giữ cơ nghiệp, không ngờ ông lại theo lời những kẻ hèn nhát đó?","Chu Du","",},
        [4416] = {8404,"Chu Du","Sáu quận Giang Đông nếu bị nạn binh đao tất quy oán cho ta, nên ta xin hàng.","Sáu quận Giang Đông nếu bị nạn binh đao tất quy oán cho ta, nên ta xin hàng.","Lỗ Túc","",},
        [4417] = {8404,"Lỗ Túc","Không thể. Đông Ngô là nơi hiểm trở, Tào Tháo chưa chắc làm gì được.","Không thể. Đông Ngô là nơi hiểm trở, Tào Tháo chưa chắc làm gì được.","Chu Du","",},
        [4418] = {8404,"Gia Cát Lượng","Ha ha ha.","Ha ha ha.","Chu Du","",},
        [4419] = {8404,"Chu Du","Tiên sinh có việc gì mà cười?","Tiên sinh có việc gì mà cười?","Gia Cát Lượng","",},
        [4420] = {8404,"Gia Cát Lượng","Ta có cười gì đâu, chỉ cười Tử Kính không thức thời.","Ta có cười gì đâu, chỉ cười Tử Kính không thức thời.","Chu Du","",},
        [4421] = {8405,"Lỗ Túc","Sao tiên sinh bảo ta không thức thời?","Sao tiên sinh bảo ta không thức thời?","Gia Cát Lượng","",},
        [4422] = {8405,"Gia Cát Lượng","Công Cẩn muốn hàng Tào, rất là hợp lẽ.","Công Cẩn muốn hàng Tào, rất là hợp lẽ.","Lỗ Túc","",},
        [4423] = {8405,"Chu Du","Khổng Minh là người thức thời, tất một lòng như ta.","Khổng Minh là người thức thời, tất một lòng như ta.","Gia Cát Lượng","",},
        [4424] = {8405,"Lỗ Túc","Khổng Minh! Sao ông lại nói thế?","Khổng Minh! Sao ông lại nói thế?","Gia Cát Lượng","",},
        [4425] = {8405,"Gia Cát Lượng","Tháo rất giỏi việc dùng binh, những kẻ dám chống lại chỉ có Lữ Bố, Viên Thiệu... Mấy người đó đều bị Tháo diệt cả rồi!","Tháo rất giỏi việc dùng binh, những kẻ dám chống lại chỉ có Lữ Bố, Viên Thiệu... Mấy người đó đều bị Tháo diệt cả rồi!","Lỗ Túc","",},
        [4426] = {8405,"Gia Cát Lượng","Chỉ có Lưu Dự Châu là dám chống lại, nay một mình ở đất Giang Hạ, sống còn chưa biết ra sao?","Chỉ có Lưu Dự Châu là dám chống lại, nay một mình ở đất Giang Hạ, sống còn chưa biết ra sao?","Lỗ Túc","",},
        [4427] = {8405,"Lỗ Túc","Ngươi bảo chúa công ta chịu nhục thuần phục quốc tặc sao!","Ngươi bảo chúa công ta chịu nhục thuần phục quốc tặc sao!","Gia Cát Lượng","",},
        [4428] = {8405,"Gia Cát Lượng","Ta có một kế, sai một người sứ đưa hai người sang sông. Tào Tháo được hai người ấy sẽ cuốn cờ rút lui ngay.","Ta có một kế, sai một người sứ đưa hai người sang sông. Tào Tháo được hai người ấy sẽ cuốn cờ rút lui ngay.","Lỗ Túc","",},
        [4429] = {8405,"Chu Du","Dùng hai người nào mà lui được quân Tào?","Dùng hai người nào mà lui được quân Tào?","Gia Cát Lượng","",},
        [4430] = {8405,"Gia Cát Lượng","Hai cô con gái của Giang Đông Kiều Công!","Hai cô con gái của Giang Đông Kiều Công!","Chu Du","",},
        [4431] = {8406,"Chu Du","Vì sao dùng hai cô con gái Giang Đông Kiều Công?","Vì sao dùng hai cô con gái Giang Đông Kiều Công?","Gia Cát Lượng","",},
        [4432] = {8406,"Gia Cát Lượng","Khi ở Long Trung, nghe tin Tháo mới dựng đài Đồng Tước trên sông Chương Hà, chọn cô nương đẹp nhốt vào đó.","Khi ở Long Trung, nghe tin Tháo mới dựng đài Đồng Tước trên sông Chương Hà, chọn cô nương đẹp nhốt vào đó.","Chu Du","",},
        [4433] = {8406,"Gia Cát Lượng","Tháo vốn háo sắc, biết bên Giang Đông có Đại Kiều và Tiểu Kiều. Hai người đều có nhan sắc chim sa cá lặn.","Tháo vốn háo sắc, biết bên Giang Đông có Đại Kiều và Tiểu Kiều. Hai người đều có nhan sắc chim sa cá lặn.","Chu Du","",},
        [4434] = {8406,"Gia Cát Lượng","Tháo từng thề sẽ lấy được hai chị em nàng Kiều ở Giang Đông, đem về đài Đồng Tước để vui tuổi già!","Tháo từng thề sẽ lấy được hai chị em nàng Kiều ở Giang Đông, đem về đài Đồng Tước để vui tuổi già!","Chu Du","",},
        [4435] = {8406,"Gia Cát Lượng","Nay Tháo chực chiếm Giang Nam, cũng chỉ vì hai cô nương ấy. Tướng quân sao không dâng hai người cho Tào Tháo","Nay Tháo chực chiếm Giang Nam, cũng chỉ vì hai cô nương ấy. Tướng quân sao không dâng hai người cho Tào Tháo","Chu Du","",},
        [4436] = {8406,"Gia Cát Lượng","Đó cũng là kế Phạm Lãi dâng Tây Thi cho Ngô Vương.","Đó cũng là kế Phạm Lãi dâng Tây Thi cho Ngô Vương.","Chu Du","",},
        [4437] = {8406,"Chu Du","Có gì làm chứng về việc Tào Tháo muốn được hai nàng Kiều?","Có gì làm chứng về việc Tào Tháo muốn được hai nàng Kiều?","Gia Cát Lượng","",},
        [4438] = {8406,"Nhân vật","Tháo sai Tào Thực viết một bài phú, ý chính trong bài phú nói về nhà Tào nếu làm thiên tử thì thề sẽ lấy cho kỳ được hai nàng Kiều.","Tháo sai Tào Thực viết một bài phú, ý chính trong bài phú nói về nhà Tào nếu làm thiên tử thì thề sẽ lấy cho kỳ được hai nàng Kiều.","Chu Du","",},
        [4439] = {8406,"Nhân vật","Ta có mang bài phú đến, mời xem qua.","Ta có mang bài phú đến, mời xem qua.","Chu Du","",},
        [4440] = {8406,"Chu Du","Lão tặc khinh người quá đáng!","Lão tặc khinh người quá đáng!","Nhân vật","",},
        [4441] = {8406,"Gia Cát Lượng","Ngày xưa Hung Nô xâm nhập, thiên tử còn đem công chúa gả cho hắn, sao tướng quân lại tiếc?","Ngày xưa Hung Nô xâm nhập, thiên tử còn đem công chúa gả cho hắn, sao tướng quân lại tiếc?","Chu Du","",},
        [4442] = {8406,"Chu Du","Ông chưa rõ Đại Kiều là thê tử Tôn Bá Phù, Tiểu Kiều là thê tử Du đó.","Ông chưa rõ Đại Kiều là thê tử Tôn Bá Phù, Tiểu Kiều là thê tử Du đó.","Gia Cát Lượng","",},
        [4443] = {8406,"Gia Cát Lượng","Ta thật vô tình, nói năng lỗ mỗ, đáng phạt!","Ta thật vô tình, nói năng lỗ mỗ, đáng phạt!","Chu Du","",},
        [4444] = {8406,"Chu Du","Ta không đội trời chung với lão tặc. Sau này gặp chúa công sẽ khởi binh.","Ta không đội trời chung với lão tặc. Sau này gặp chúa công sẽ khởi binh.","Gia Cát Lượng","",},
        [4445] = {8407,"Tôn Quyền","Mấy nay bàn luận, người khuyên ta nên hàng, kẻ lại bảo nên đánh, xin Công Cẩn quyết định cho.","Mấy nay bàn luận, người khuyên ta nên hàng, kẻ lại bảo nên đánh, xin Công Cẩn quyết định cho.","Chu Du","",},
        [4446] = {8407,"Chu Du","Ai khuyên chúa công hàng?","Ai khuyên chúa công hàng?","Tôn Quyền","",},
        [4447] = {8407,"Tôn Quyền","Bọn Trương Tử Bố đều muốn hàng.","Bọn Trương Tử Bố đều muốn hàng.","Chu Du","",},
        [4448] = {8407,"Chu Du","Muốn nghe ý kiến xin hàng.","Muốn nghe ý kiến xin hàng.","Tôn Quyền","",},
        [4449] = {8407,"Trương Chiêu","Tào Tháo mượn danh triều đình dẹp đánh bốn phương, uy thế lớn mạnh.","Tào Tháo mượn danh triều đình dẹp đánh bốn phương, uy thế lớn mạnh.","Chu Du","",},
        [4450] = {8407,"Trương Chiêu","Tào Tháo có nhiều thuyền, ta lấy gì chống được. Chi bằng hãy hàng, sau tính kế khác.","Tào Tháo có nhiều thuyền, ta lấy gì chống được. Chi bằng hãy hàng, sau tính kế khác.","Chu Du","",},
        [4451] = {8407,"Chu Du","Giang Đông từ khi lập nước đến giờ, đã trải ba đời, sao nỡ chốc lát bỏ mất?","Giang Đông từ khi lập nước đến giờ, đã trải ba đời, sao nỡ chốc lát bỏ mất?","Trương Chiêu","",},
        [4452] = {8407,"Tôn Quyền","Thế đánh thì có kế gì?","Thế đánh thì có kế gì?","Chu Du","",},
        [4453] = {8407,"Chu Du","Tướng quân là bậc thần võ hùng tài, đáng lẽ tung hoành cả trong thiên hạ, sao lại phải hàng giặc?","Tướng quân là bậc thần võ hùng tài, đáng lẽ tung hoành cả trong thiên hạ, sao lại phải hàng giặc?","Tôn Quyền","",},
        [4454] = {8407,"Chu Du","Tháo đi chuyến này, đất bắc chưa yên, mà đánh phương Nam, là một điều kỵ. Quân Bắc không quen đánh dưới nước là điều kỵ thứ hai.","Tháo đi chuyến này, đất bắc chưa yên, mà đánh phương Nam, là một điều kỵ. Quân Bắc không quen đánh dưới nước là điều kỵ thứ hai.","Tôn Quyền","",},
        [4455] = {8407,"Chu Du","Mùa đông ngựa không có cỏ ăn là ba điều kỵ, đưa quân bộ tiến vào vùng sông nước, nhiều người đau ốm là bốn điều kỵ.","Mùa đông ngựa không có cỏ ăn là ba điều kỵ, đưa quân bộ tiến vào vùng sông nước, nhiều người đau ốm là bốn điều kỵ.","Tôn Quyền","",},
        [4456] = {8407,"Chu Du","Quân Tháo phạm bốn điều kỵ ấy. Du xin vài vạn tinh binh đến đóng ở Hạ Khẩu, đủ phá được Tào Tháo.","Quân Tháo phạm bốn điều kỵ ấy. Du xin vài vạn tinh binh đến đóng ở Hạ Khẩu, đủ phá được Tào Tháo.","Tôn Quyền","",},
        [4457] = {8407,"Tôn Quyền","Phong ngươi làm đại đô đốc. Trình Phổ làm phó đô đốc khởi binh diệt Tào!","Phong ngươi làm đại đô đốc. Trình Phổ làm phó đô đốc khởi binh diệt Tào!","Chu Du","",},
        [4458] = {8501,"Chu Du","Hôm nay trong phủ bàn định đã xong, xin tiên sinh hiến kế hay để phá Tào Tháo.","Hôm nay trong phủ bàn định đã xong, xin tiên sinh hiến kế hay để phá Tào Tháo.","Gia Cát Lượng","",},
