        [4467] = {8502,"Chu Du","Nay xét ra hắn chỉ huy động được chục vạn quân, nhưng đa số vẫn còn nghi ngờ chưa phục.","Nay xét ra hắn chỉ huy động được chục vạn quân, nhưng đa số vẫn còn nghi ngờ chưa phục.","Tôn Quyền","",},
        [4468] = {8502,"Tôn Quyền","Công Cẩn quả thật đã gỡ được mối hoài nghi cho ta. Chỉ có ngươi và Tử Kính là hợp bụng ta.","Công Cẩn quả thật đã gỡ được mối hoài nghi cho ta. Chỉ có ngươi và Tử Kính là hợp bụng ta.","Chu Du","",},
