        [4702] = {8808,"Hoàng Cái","Sao ông biết?","Sao ông biết?","Khám Trạch","",},
        [4703] = {8808,"Khám Trạch","Ta xem bộ dạng Công Cẩn, mười phần đã đoán được chín.","Ta xem bộ dạng Công Cẩn, mười phần đã đoán được chín.","Hoàng Cái","",},
        [4704] = {8808,"Hoàng Cái","Ta chịu hậu ân họ Tôn đã ba đời, không lấy gì báo được, nên hiến kế ấy để phá Tào Tháo.","Ta chịu hậu ân họ Tôn đã ba đời, không lấy gì báo được, nên hiến kế ấy để phá Tào Tháo.","Khám Trạch","",},
        [4705] = {8808,"Hoàng Cái","Ta xem trong đám tướng sĩ, không ai là tâm phúc của, có ông là người trung nghĩa, nên mới dám thổ lộ can trường.","Ta xem trong đám tướng sĩ, không ai là tâm phúc của, có ông là người trung nghĩa, nên mới dám thổ lộ can trường.","Khám Trạch","",},
        [4706] = {8808,"Khám Trạch","Phải chăng muốn dùng ta dâng thư trá hàng.","Phải chăng muốn dùng ta dâng thư trá hàng.","Hoàng Cái","",},
        [4707] = {8808,"Hoàng Cái","Quả thật có ý đó, ông có chịu giúp không?","Quả thật có ý đó, ông có chịu giúp không?","Khám Trạch","",},
        [4708] = {8808,"Khám Trạch","Đại trượng phu không lập công danh, chẳng hoá ra bằng cỏ cây mục nát? Ông đã quên mình báo chúa, đây ta dám há tiếc thân!!","Đại trượng phu không lập công danh, chẳng hoá ra bằng cỏ cây mục nát? Ông đã quên mình báo chúa, đây ta dám há tiếc thân!!","Hoàng Cái","",},
        [4709] = {8808,"Hoàng Cái","Không biết đa tạ thế nào, chỉ còn cách lạy tạ.","Không biết đa tạ thế nào, chỉ còn cách lạy tạ.","Khám Trạch","",},
        [4710] = {8808,"Khám Trạch","Việc không nên để lâu, xin đi ngay hôm nay!","Việc không nên để lâu, xin đi ngay hôm nay!","Hoàng Cái","",},
        [4711] = {8808,"Hoàng Cái","Thư đã viết sẵn rồi.","Thư đã viết sẵn rồi.","Khám Trạch","",},
        [4712] = {8808,"Khám Trạch","Ta giả làm ông lão đánh cá, bơi thuyền con sang thẳng bờ phía bắc.","Ta giả làm ông lão đánh cá, bơi thuyền con sang thẳng bờ phía bắc.","Hoàng Cái","",},
        [4713] = {8809,"Tào Tháo","Ngươi là tham mưu Đông Ngô, đến đây làm gì?","Ngươi là tham mưu Đông Ngô, đến đây làm gì?","Khám Trạch","",},
        [4714] = {8809,"Khám Trạch","Hoàng Công Phúc là cựu thần ba đời bên Đông Ngô, nay vô cớ bị Chu Du dập đánh khổ sở trước mặt các tướng. Không biết thừa tướng chịu thu nạp không?","Hoàng Công Phúc là cựu thần ba đời bên Đông Ngô, nay vô cớ bị Chu Du dập đánh khổ sở trước mặt các tướng. Không biết thừa tướng chịu thu nạp không?","Tào Tháo","",},
        [4715] = {8809,"Tào Tháo","Hoàng Cái dùng khổ nhục kế, sai người đến dâng thư trá hàng! Lôi ra trảm.","Hoàng Cái dùng khổ nhục kế, sai người đến dâng thư trá hàng! Lôi ra trảm.","Khám Trạch","",},
        [4716] = {8809,"Khám Trạch","Ha ha ha.","Ha ha ha.","Tào Tháo","",},
        [4717] = {8809,"Tào Tháo","Ta đã biết rõ gian kế, ngươi còn cười gì nữa?","Ta đã biết rõ gian kế, ngươi còn cười gì nữa?","Khám Trạch","",},
        [4718] = {8809,"Khám Trạch","Ta đâu dám cười! Ta cười Hoàng Công Phúc không biết người đó thôi!","Ta đâu dám cười! Ta cười Hoàng Công Phúc không biết người đó thôi!","Tào Tháo","",},
        [4719] = {8809,"Tào Tháo","Ta đây học binh thư từ nhỏ, còn lạ gì những mẹo ấy, ngươi lừa ai thì được, chớ lừa ta sao nổi.","Ta đây học binh thư từ nhỏ, còn lạ gì những mẹo ấy, ngươi lừa ai thì được, chớ lừa ta sao nổi.","Khám Trạch","",},
        [4720] = {8809,"Khám Trạch","Thôi, biết điều thì mau thu quân về, đừng có đánh chác gì nữa mà bị Chu Du tóm được. Đồ vô học!","Thôi, biết điều thì mau thu quân về, đừng có đánh chác gì nữa mà bị Chu Du tóm được. Đồ vô học!","Tào Tháo","",},
        [4721] = {8809,"Tào Tháo","Sao lại bảo ta là vô học?","Sao lại bảo ta là vô học?","Khám Trạch","",},
        [4722] = {8809,"Khám Trạch","Trốn chúa đi lén, không thể hẹn giờ? Nếu hẹn trước mà lỡ ra việc trong chưa xong, ngoài đã có binh tiếp ứng, chẳng hoá ra lộ chuyện mất?","Trốn chúa đi lén, không thể hẹn giờ? Nếu hẹn trước mà lỡ ra việc trong chưa xong, ngoài đã có binh tiếp ứng, chẳng hoá ra lộ chuyện mất?","Tào Tháo","",},
        [4723] = {8809,"Khám Trạch","Do đó gặp lúc thuận tiện thì làm, chứ hẹn trước làm sao được. Ngươi không hiểu lẽ đó, quả là đồ vô học.","Do đó gặp lúc thuận tiện thì làm, chứ hẹn trước làm sao được. Ngươi không hiểu lẽ đó, quả là đồ vô học.","Tào Tháo","",},
        [4724] = {8809,"Tào Tháo","Ta xét việc không tinh, lỡ phạm tôn nghiêm, xin tha thứ cho.","Ta xét việc không tinh, lỡ phạm tôn nghiêm, xin tha thứ cho.","Khám Trạch","",},
        [4725] = {8809,"Khám Trạch","Ta với Hoàng Công Phúc đã đến hàng, còn có điều gì là dối trá nữa.","Ta với Hoàng Công Phúc đã đến hàng, còn có điều gì là dối trá nữa.","Tào Tháo","",},
        [4726] = {8809,"Tào Tháo","Nếu hai người lập được công to, ngày sau sẽ được phong tước cao hơn những người khác.","Nếu hai người lập được công to, ngày sau sẽ được phong tước cao hơn những người khác.","Khám Trạch","",},
        [4727] = {8809,"Khám Trạch","Bọn ta có phải vì tước lộc mà lại đây đâu, chính vì thuận lẽ trời, hợp lòng người đó thôi.","Bọn ta có phải vì tước lộc mà lại đây đâu, chính vì thuận lẽ trời, hợp lòng người đó thôi.","Tào Tháo","",},
        [4728] = {8810,"Khám Trạch","Hôm qua tướng quân cứu Hoàng Công Phúc mà cũng bị đánh, ta rất căm tức.","Hôm qua tướng quân cứu Hoàng Công Phúc mà cũng bị đánh, ta rất căm tức.","Cam Ninh","",},
        [4729] = {8810,"Cam Ninh","Công Cẩn chẳng coi chúng ta ra gì. Ta bị làm nhục thật xấu hổ với mọi người bên sông Trường Giang.","Công Cẩn chẳng coi chúng ta ra gì. Ta bị làm nhục thật xấu hổ với mọi người bên sông Trường Giang.","Khám Trạch","",},
        [4730] = {8810,"Thái Hòa","Chẳng hay tướng quân và tiên sinh sao buồn bực làm vậy?","Chẳng hay tướng quân và tiên sinh sao buồn bực làm vậy?","Cam Ninh","",},
        [4731] = {8810,"Cam Ninh","Các ngươi biết thế nào được những điều đau khổ trong lòng bọn ta!","Các ngươi biết thế nào được những điều đau khổ trong lòng bọn ta!","Thái Hòa","",},
        [4732] = {8810,"Thái Hòa","Không lẽ muốn phản Đông Ngô hàng Tào Tháo?","Không lẽ muốn phản Đông Ngô hàng Tào Tháo?","Cam Ninh","",},
        [4733] = {8810,"Cam Ninh","Việc của ta bị lộ rồi, phải diệt khẩu!","Việc của ta bị lộ rồi, phải diệt khẩu!","Thái Hòa","",},
        [4734] = {8810,"Thái Hòa","Hai ông chớ lo, ta cũng xin đem việc tâm phúc bày tỏ với các ông","Hai ông chớ lo, ta cũng xin đem việc tâm phúc bày tỏ với các ông","Cam Ninh","",},
        [4735] = {8810,"Cam Ninh","Nói mau lên!","Nói mau lên!","Thái Hòa","",},
        [4736] = {8810,"Thái Hòa","Tào công sai hai bọn ta đến trá hàng, nếu hai ông có ý quy thuận Tào công, bọn ta xin dẫn tiến.","Tào công sai hai bọn ta đến trá hàng, nếu hai ông có ý quy thuận Tào công, bọn ta xin dẫn tiến.","Cam Ninh","",},
        [4737] = {8810,"Cam Ninh","Ngươi nói thật?","Ngươi nói thật?","Thái Hòa","",},
        [4738] = {8810,"Thái Hòa","Đâu dám nói dối!","Đâu dám nói dối!","Cam Ninh","",},
        [4739] = {8810,"Cam Ninh","Nếu được như thế thì trời cũng giúp ta!","Nếu được như thế thì trời cũng giúp ta!","Thái Hòa","",},
        [4740] = {8810,"Thái Hòa","Việc Hoàng Công Phúc và tướng quân bị nhục, ta đã báo tin về cho thừa tướng rồi.","Việc Hoàng Công Phúc và tướng quân bị nhục, ta đã báo tin về cho thừa tướng rồi.","Cam Ninh","",},
        [4741] = {8810,"Khám Trạch","Ta đã dâng thư hàng của Công Phúc cho thừa tướng, nay đến gọi Hưng Bá cùng đến đầu hàng.","Ta đã dâng thư hàng của Công Phúc cho thừa tướng, nay đến gọi Hưng Bá cùng đến đầu hàng.","Thái Hòa","",},
        [4742] = {8810,"Cam Ninh","Đại trượng phu gặp minh chủ chắc chắn sẽ theo.","Đại trượng phu gặp minh chủ chắc chắn sẽ theo.","Thái Hòa","",},
        [4743] = {8811,"Tào Tháo","Bên Trường Giang, Cam Ninh bị Chu Du làm nhục, xin làm nội ứng, Hoàng Cái bị phạt, sai Khám Trạch sang xin hàng, đều chưa đáng tin lắm.","Bên Trường Giang, Cam Ninh bị Chu Du làm nhục, xin làm nội ứng, Hoàng Cái bị phạt, sai Khám Trạch sang xin hàng, đều chưa đáng tin lắm.","","",},
        [4744] = {8811,"Tào Tháo","Ai dám sang trại Chu Du dò xem hư thực ra sao?","Ai dám sang trại Chu Du dò xem hư thực ra sao?","","",},
        [4745] = {8811,"Tưởng Can","Ta bữa trước sang Đông Ngô, chưa làm được việc gì, vẫn còn áy náy. Nay xin liều đi chuyến nữa, quyết dò được sự thật về báo với thừa tướng.","Ta bữa trước sang Đông Ngô, chưa làm được việc gì, vẫn còn áy náy. Nay xin liều đi chuyến nữa, quyết dò được sự thật về báo với thừa tướng.","Tào Tháo","",},
        [4746] = {8812,"Chu Du","Tử Dực sao dám khinh ta thế?","Tử Dực sao dám khinh ta thế?","Tưởng Can","",},
        [4747] = {8812,"Tưởng Can","Ta nghĩ ông là chỗ tình nghĩa huynh đệ cũ, muốn đến bày tỏ tâm sự, sao lại bảo ta lừa lọc?","Ta nghĩ ông là chỗ tình nghĩa huynh đệ cũ, muốn đến bày tỏ tâm sự, sao lại bảo ta lừa lọc?","Chu Du","",},
        [4748] = {8812,"Chu Du","Ngươi muốn dụ ta hàng Tào, trừ phi sông cạn đá mòn! Lần trước ta nể tình bạn cũ, mời uống rượu và ngủ chung giường.","Ngươi muốn dụ ta hàng Tào, trừ phi sông cạn đá mòn! Lần trước ta nể tình bạn cũ, mời uống rượu và ngủ chung giường.","Tưởng Can","",},
        [4749] = {8812,"Chu Du","Không ngờ ngươi ăn cắp thư của ta, lẻn về báo với Tào Tháo, để Trương Doãn, Thái Mạo bị diệt.","Không ngờ ngươi ăn cắp thư của ta, lẻn về báo với Tào Tháo, để Trương Doãn, Thái Mạo bị diệt.","Tưởng Can","",},
        [4750] = {8812,"Chu Du","Nếu không nghĩ đến tình xưa, ta đã tống ngươi đi rồi, nhưng sắp đánh Tào Tháo nên giữ ngươi lại, kẻo lộ quân cơ!","Nếu không nghĩ đến tình xưa, ta đã tống ngươi đi rồi, nhưng sắp đánh Tào Tháo nên giữ ngươi lại, kẻo lộ quân cơ!","Tưởng Can","",},
        [4751] = {8812,"Chu Du","Đợi khi nào phá xong Tào Tháo, ta sẽ tiễn ngươi sang sông.","Đợi khi nào phá xong Tào Tháo, ta sẽ tiễn ngươi sang sông.","Tưởng Can","",},
