        [4774] = {8902,"Bàng Thống","Phương pháp huấn luyện thủy quân của thừa tướng thật tuyệt diệu, nhưng tiếc chưa được toàn diện.","Phương pháp huấn luyện thủy quân của thừa tướng thật tuyệt diệu, nhưng tiếc chưa được toàn diện.","Tào Tháo","",},
