        [4776] = {8902,"Bàng Thống","Ở sông lớn, sóng gió liên miên. Người phương bắc không quen ngồi thuyền bị sóng đánh nghiêng ngả mới sinh ra bệnh.","Ở sông lớn, sóng gió liên miên. Người phương bắc không quen ngồi thuyền bị sóng đánh nghiêng ngả mới sinh ra bệnh.","Tào Tháo","",},
        [4777] = {8902,"Bàng Thống","Nếu đem thuyền lớn nhỏ, ghép lại thành từng cụm, đầu đuôi dùng xích sắt ghép liền với nhau, trên thuyền lát ván phẳng phiu.","Nếu đem thuyền lớn nhỏ, ghép lại thành từng cụm, đầu đuôi dùng xích sắt ghép liền với nhau, trên thuyền lát ván phẳng phiu.","Tào Tháo","",},
        [4778] = {8902,"Bàng Thống","Như thế chẳng những người đi lại dễ dàng, mà đến ngựa chạy cũng được, không còn lo chi thủy triều và sóng gió nữa.","Như thế chẳng những người đi lại dễ dàng, mà đến ngựa chạy cũng được, không còn lo chi thủy triều và sóng gió nữa.","Tào Tháo","",},
        [4779] = {8902,"Tào Tháo","Nhờ kế hay của tiên sinh mới có thể phá được Đông Ngô!","Nhờ kế hay của tiên sinh mới có thể phá được Đông Ngô!","Bàng Thống","",},
        [4780] = {8902,"Bàng Thống","Ta cũng mới nghĩ nông cạn, xin thừa tướng xem thử có nên không.","Ta cũng mới nghĩ nông cạn, xin thừa tướng xem thử có nên không.","Tào Tháo","",},
        [4781] = {8902,"Tào Tháo","Lập tức truyền thợ rèn, ngày đêm đúc dây xích và ván lớn ghép chặt các thuyền lại.","Lập tức truyền thợ rèn, ngày đêm đúc dây xích và ván lớn ghép chặt các thuyền lại.","Bàng Thống","",},
        [4782] = {8903,"Từ Thứ","Hoàng Cái dùng khổ nhục kế, ngươi lại đến bày liên hoàn kế, còn không phải định dùng hỏa công hay sao!?","Hoàng Cái dùng khổ nhục kế, ngươi lại đến bày liên hoàn kế, còn không phải định dùng hỏa công hay sao!?","Bàng Thống","",},
        [4783] = {8903,"Từ Thứ","Bọn ngươi mưu mô ác độc, chỉ lừa được Tào Tháo, sao che nổi mắt ta!","Bọn ngươi mưu mô ác độc, chỉ lừa được Tào Tháo, sao che nổi mắt ta!","Bàng Thống","",},
        [4784] = {8903,"Bàng Thống","Nếu ngươi làm lộ kế của ta, thì nhân dân tám mươi mốt châu bên Giang Nam đều do anh hại cả!","Nếu ngươi làm lộ kế của ta, thì nhân dân tám mươi mốt châu bên Giang Nam đều do anh hại cả!","Từ Thứ","",},
