        [4793] = {8904,"Tào Tháo","Ta dẫn quân sang đánh, lúc nào cũng lo lắng về bọn Mã Đằng, Hàn Toại. Tin đồn chưa biết hư thực, nhưng cũng nên đề phòng.","Ta dẫn quân sang đánh, lúc nào cũng lo lắng về bọn Mã Đằng, Hàn Toại. Tin đồn chưa biết hư thực, nhưng cũng nên đề phòng.","","",},
        [4794] = {8904,"Từ Thứ","Từ khi được thừa tướng thu dùng, chưa lập được công lao nào. Ta xin ra giữ ải Tản Quan, nếu có khẩn cấp sẽ báo cho thừa tướng.","Từ khi được thừa tướng thu dùng, chưa lập được công lao nào. Ta xin ra giữ ải Tản Quan, nếu có khẩn cấp sẽ báo cho thừa tướng.","Tào Tháo","",},
        [4795] = {8904,"Tào Tháo","Nếu Nguyên Trực chịu khó đi cho, ta cấp ba nghìn quân mã bộ, cùng Tang Bá làm tiên phong, lập tức đi ngay.","Nếu Nguyên Trực chịu khó đi cho, ta cấp ba nghìn quân mã bộ, cùng Tang Bá làm tiên phong, lập tức đi ngay.","Từ Thứ","",},
