        [4849] = {9105,"Lỗ Túc","Đô đốc, cờ quạt tung bay, đuôi cờ bay về phía tây bắc. Đột nhiên nổi gió Đông Nam.","Đô đốc, cờ quạt tung bay, đuôi cờ bay về phía tây bắc. Đột nhiên nổi gió Đông Nam.","Chu Du","",},
        [4850] = {9105,"Chu Du","Người này có phép đoạt được trời đất. Nếu để sống sẽ gây vạ lớn cho Đông Ngô, chi bằng diệt trừ hậu hoạn.","Người này có phép đoạt được trời đất. Nếu để sống sẽ gây vạ lớn cho Đông Ngô, chi bằng diệt trừ hậu hoạn.","Lỗ Túc","",},
        [4851] = {9105,"Chu Du","Đinh Phụng, Từ Thịnh, hai người dẫn một trăm quân, Từ Thịnh đi đường thủy, Đinh Phụng đi đường bộ,","Đinh Phụng, Từ Thịnh, hai người dẫn một trăm quân, Từ Thịnh đi đường thủy, Đinh Phụng đi đường bộ,","Lỗ Túc","",},
        [4852] = {9105,"Chu Du","Cùng đến núi Nam Bình, xử lý Gia Cát Lượng, đem thủ cấp về đây nhận thưởng.","Cùng đến núi Nam Bình, xử lý Gia Cát Lượng, đem thủ cấp về đây nhận thưởng.","Lỗ Túc","",},
