        [4902] = {9302,"Quan Vũ","Quan Mỗ theo huynh trưởng chinh chiến nhiều năm. Hôm nay gặp địch mạnh, quân sư không dùng là có ý gì?","Quan Mỗ theo huynh trưởng chinh chiến nhiều năm. Hôm nay gặp địch mạnh, quân sư không dùng là có ý gì?","Gia Cát Lượng","",},
        [4903] = {9302,"Gia Cát Lượng","Vân Trường đừng trách! Ta vốn định nhờ túc hạ giữ một cửa ải quan trọng nhất, nhưng có chút trở ngại không dám điều đi.","Vân Trường đừng trách! Ta vốn định nhờ túc hạ giữ một cửa ải quan trọng nhất, nhưng có chút trở ngại không dám điều đi.","Quan Vũ","",},
        [4904] = {9302,"Quan Vũ","Có trở ngại gì? Đồng ý gặp Du.","Có trở ngại gì? Đồng ý gặp Du.","Gia Cát Lượng","",},
        [4905] = {9302,"Gia Cát Lượng","Trước đây Tào Tháo đối xử tốt với túc hạ, túc hạ đương nhiên báo đáp. Nay Tháo dẫn binh thua, chắc chắn đi Hoa Dung Đạo, nếu lệnh túc hạ đi, chắc chắn cho hắn đi qua.","Trước đây Tào Tháo đối xử tốt với túc hạ, túc hạ đương nhiên báo đáp. Nay Tháo dẫn binh thua, chắc chắn đi Hoa Dung Đạo, nếu lệnh túc hạ đi, chắc chắn cho hắn đi qua.","Quan Vũ","",},
        [4906] = {9302,"Quan Vũ","Quân sư suy nghĩ nhiều rồi! Ngày trước Tào Tháo đúng là trọng đãi ta, ta đã trảm Nhan Lương, diệt Văn Xú, giải Bạch Mã Chi Vi báo đáp hắn. Hôm nay gặp, tại sao lại phải tha!","Quân sư suy nghĩ nhiều rồi! Ngày trước Tào Tháo đúng là trọng đãi ta, ta đã trảm Nhan Lương, diệt Văn Xú, giải Bạch Mã Chi Vi báo đáp hắn. Hôm nay gặp, tại sao lại phải tha!","Gia Cát Lượng","",},
