        [4925] = {9402,"Tào Tháo","Ta cười Gia Cát Lượng, Chu Du trí mưu không đủ. Nếu ta dụng binh, thì ngay nơi này, cũng mai phục binh mã, đợi quân địch mệt rồi tấn công.","Ta cười Gia Cát Lượng, Chu Du trí mưu không đủ. Nếu ta dụng binh, thì ngay nơi này, cũng mai phục binh mã, đợi quân địch mệt rồi tấn công.","Trương Liêu","",},
        [4926] = {9402,"Tào Tháo","Chúng ta cho dù thoát hiểm cũng khó tránh trọng thương. Ta cười là vì vậy.","Chúng ta cho dù thoát hiểm cũng khó tránh trọng thương. Ta cười là vì vậy.","Trương Liêu","",},
        [4927] = {9402,"Trương Phi","Ai nói Khổng Minh không đủ mưu trí, Dực Đức ở đây chờ đã lâu!","Ai nói Khổng Minh không đủ mưu trí, Dực Đức ở đây chờ đã lâu!","Tào Tháo","",},
        [4928] = {9402,"Trương Liêu","Thừa tướng đi trước, ta đến yểm hộ.","Thừa tướng đi trước, ta đến yểm hộ.","Trương Phi","",},
        [4929] = {9403,"Trương Liêu","Phía trước có hai ngã, xin hỏi đi ngã nào?","Phía trước có hai ngã, xin hỏi đi ngã nào?","Tào Tháo","",},
        [4930] = {9403,"Tào Tháo","Con đường nào gần?","Con đường nào gần?","Trương Liêu","",},
        [4931] = {9403,"Trương Liêu","Đường lớn bằng phẳng, nhưng xa hơn 50 dặm. Đường nhỏ vào Hoa Dung Đạo, nhưng gần hơn 50 dặm, chỉ là đường hẹp hiểm trở khó đi.","Đường lớn bằng phẳng, nhưng xa hơn 50 dặm. Đường nhỏ vào Hoa Dung Đạo, nhưng gần hơn 50 dặm, chỉ là đường hẹp hiểm trở khó đi.","Tào Tháo","",},
        [4932] = {9403,"Trương Liêu","Hơn nữa bên vách núi đường nhỏ có vài nơi có khói, đường lớn thì không có động tĩnh gì.","Hơn nữa bên vách núi đường nhỏ có vài nơi có khói, đường lớn thì không có động tĩnh gì.","Tào Tháo","",},
        [4933] = {9403,"Tào Tháo","Đi đường nhỏ Hoa Dung Đạo.","Đi đường nhỏ Hoa Dung Đạo.","Trương Liêu","",},
        [4934] = {9403,"Trương Liêu","Có khói hiệu, tất có binh mã, sao phải quay về đường này?","Có khói hiệu, tất có binh mã, sao phải quay về đường này?","Tào Tháo","",},
        [4935] = {9403,"Tào Tháo","Không nghe binh thư nói sao: Hư tắc thực chi, thực tắc hư chi.","Không nghe binh thư nói sao: Hư tắc thực chi, thực tắc hư chi.","Trương Liêu","",},
        [4936] = {9403,"Tào Tháo","Gia Cát Lượng đa mưu, sai người ở vách núi tạo khói, khiến quân ta không dám đi con đường núi này, hắn phục binh chờ ở đường lớn. Ta đã quyết định, không để trúng kế hắn!","Gia Cát Lượng đa mưu, sai người ở vách núi tạo khói, khiến quân ta không dám đi con đường núi này, hắn phục binh chờ ở đường lớn. Ta đã quyết định, không để trúng kế hắn!","Trương Liêu","",},
        [4937] = {9403,"Trương Liêu","Thừa tướng diệu toán, không ai sánh bằng.","Thừa tướng diệu toán, không ai sánh bằng.","Tào Tháo","",},
        [4938] = {9404,"Trương Liêu","Không được, hai bên có 500 Đao Thủ, thủ Đại Tướng Quan Vân Trường, cầm Thanh Long Đao, cưỡi ngựa Xích Thố, cản trở đường đi.","Không được, hai bên có 500 Đao Thủ, thủ Đại Tướng Quan Vân Trường, cầm Thanh Long Đao, cưỡi ngựa Xích Thố, cản trở đường đi.","Tào Tháo","",},
        [4939] = {9404,"Tào Tháo","Đã đến đây, phải quyết một trận tử chiến!","Đã đến đây, phải quyết một trận tử chiến!","Trương Liêu","",},
        [4940] = {9404,"Trình Dục","Ta biết Vân Trường ngạo thượng mà không nhẫn hạ, bắt nạt kẻ mạnh chứ không ức hiếp kẻ yếu, ân oán phân minh, là người tín nghĩa.","Ta biết Vân Trường ngạo thượng mà không nhẫn hạ, bắt nạt kẻ mạnh chứ không ức hiếp kẻ yếu, ân oán phân minh, là người tín nghĩa.","Tào Tháo","",},
        [4941] = {9404,"Trình Dục","Thừa tướng ngày trước có ơn với người, nay đích thân nói làm sao để thoát nạn.","Thừa tướng ngày trước có ơn với người, nay đích thân nói làm sao để thoát nạn.","Tào Tháo","",},
        [4942] = {9405,"Tào Tháo","Tướng quân khỏe chứ!","Tướng quân khỏe chứ!","Quan Vũ","",},
        [4943] = {9405,"Quan Vũ","Quan mỗ phụng lệnh, đợi thừa tướng đã lâu.","Quan mỗ phụng lệnh, đợi thừa tướng đã lâu.","Tào Tháo","",},
        [4944] = {9405,"Tào Tháo","Tào Tháo binh bại thế nguy, đến đây hết đường, mong tướng quân nể tình ngày xưa.","Tào Tháo binh bại thế nguy, đến đây hết đường, mong tướng quân nể tình ngày xưa.","Quan Vũ","",},
        [4945] = {9405,"Quan Vũ","Quan mỗ tuy nhận hậu ân từ thừa tướng, đã trảm Nhan Lương, diệt Văn Xú, giải vây Bạch Mã báo đáp. Việc hôm nay, sao dám lấy tư phế công?","Quan mỗ tuy nhận hậu ân từ thừa tướng, đã trảm Nhan Lương, diệt Văn Xú, giải vây Bạch Mã báo đáp. Việc hôm nay, sao dám lấy tư phế công?","Tào Tháo","",},
        [4946] = {9405,"Tào Tháo","Lúc ngũ quan trảm tướng, có còn nhớ không? Đại trượng phu lấy tín nghĩa làm trọng. Tướng quân hiểu rõ Xuân Thu chẳng lẽ không biết chuyện Dữu Công Chi Tư truy Tử Trạc Nhũ Tử?","Lúc ngũ quan trảm tướng, có còn nhớ không? Đại trượng phu lấy tín nghĩa làm trọng. Tướng quân hiểu rõ Xuân Thu chẳng lẽ không biết chuyện Dữu Công Chi Tư truy Tử Trạc Nhũ Tử?","Quan Vũ","",},
        [4947] = {9405,"Quan Vũ","Tướng sĩ giải tán.","Tướng sĩ giải tán.","Tào Tháo","",},
        [4948] = {9405,"Tào Tháo","Đa tạ tướng quân.","Đa tạ tướng quân.","Quan Vũ","",},
        [4949] = {9501,"Tào Tháo","Phía Nam dâng một con gà rừng, gà rừng rất thích lông của mình, nhìn thấy ảnh phản chiếu dưới nước sẽ nhảy múa.","Phía Nam dâng một con gà rừng, gà rừng rất thích lông của mình, nhìn thấy ảnh phản chiếu dưới nước sẽ nhảy múa.","","",},
        [4950] = {9501,"Tào Tháo","Ta muốn nó nhảy múa nhưng không có cách nào.","Ta muốn nó nhảy múa nhưng không có cách nào.","","",},
