        [4952] = {9501,"Tào Tháo","Ừ, cũng là một cách.","Ừ, cũng là một cách.","Tào Xung","",},
        [4953] = {9502,"Tào Tháo","Ta để một cái gương lớn trước mặt gà rừng, gà thấy gương liền bắt đầu nhảy múa.","Ta để một cái gương lớn trước mặt gà rừng, gà thấy gương liền bắt đầu nhảy múa.","Tào Xung","",},
