        [4955] = {9502,"Tào Xung","Có lẽ đó chính là nhìn thân thương phận.","Có lẽ đó chính là nhìn thân thương phận.","Tào Tháo","",},
        [4956] = {9503,"Hoàn Phu Nhân","Mạnh Đức, Tào Xung đột nhiên trọng bệnh, ngươi mau đi xem","Mạnh Đức, Tào Xung đột nhiên trọng bệnh, ngươi mau đi xem","Tào Tháo","",},
