        [4958] = {9504,"Tào Tháo","Có mời đại phu xem chưa?","Có mời đại phu xem chưa?","Hoàn Phu Nhân","",},
        [4959] = {9504,"Hoàn Phu Nhân","Đã nhờ đại phu xem, không biết là bệnh gì.","Đã nhờ đại phu xem, không biết là bệnh gì.","Tào Tháo","",},
        [4960] = {9504,"Tào Tháo","Yên tâm, ta tìm hết danh y khắp thiên hạ cũng phải chữa bệnh cho Tào Xung.","Yên tâm, ta tìm hết danh y khắp thiên hạ cũng phải chữa bệnh cho Tào Xung.","Hoàn Phu Nhân","",},
