        [4962] = {9504,"Tào Nhân","Vâng!","Vâng!","Tào Tháo","",},
        [4963] = {9505,"Tào Phi","Tào Xung Qua Đời, là do không may, đừng quá đau buồn.","Tào Xung Qua Đời, là do không may, đừng quá đau buồn.","Tào Tháo","",},
        [4964] = {9505,"Tào Tháo","Kiến An năm thứ 2, Tào Ngang theo ta xuất chinh Trương Tú, phụ trách đoạn hậu mà qua đời.","Kiến An năm thứ 2, Tào Ngang theo ta xuất chinh Trương Tú, phụ trách đoạn hậu mà qua đời.","Tào Phi","",},
        [4965] = {9505,"Tào Tháo","Tào Xung vô cùng thông minh, mới 13 tuổi đã qua đời, sao ta có thể không đau lòng!","Tào Xung vô cùng thông minh, mới 13 tuổi đã qua đời, sao ta có thể không đau lòng!","Tào Phi","",},
        [4966] = {9505,"Tào Tháo","Đó là bất hạnh của ta, nhưng là may mắn của Nhữ Tào.","Đó là bất hạnh của ta, nhưng là may mắn của Nhữ Tào.","Tào Phi","",},
        [4967] = {9601,"Chu Du","Dự Châu chuyển binh đến đây, lẽ nào có ý chiếm Nam Quận?","Dự Châu chuyển binh đến đây, lẽ nào có ý chiếm Nam Quận?","Lưu Bị","",},
        [4968] = {9601,"Lưu Bị","Nghe tin Đô Đốc muốn chiếm Nam Quận, cố tình đến tương trợ. Nếu Đô Đốc không dám, Bị sẽ chiếm.","Nghe tin Đô Đốc muốn chiếm Nam Quận, cố tình đến tương trợ. Nếu Đô Đốc không dám, Bị sẽ chiếm.","Chu Du","",},
        [4969] = {9601,"Chu Du","Đông Ngô ta muốn thôn tính Hán Giang, nay Nam Quận đã nằm trong tay, sao lại không dám?","Đông Ngô ta muốn thôn tính Hán Giang, nay Nam Quận đã nằm trong tay, sao lại không dám?","Lưu Bị","",},
        [4970] = {9601,"Lưu Bị","Thắng thua không thể dự đoán. Tào Tháo trước khi về, ra lệnh Tào Nhân thủ Nam Quận, chắc chắn có mưu kế, cộng thêm Tào Nhân dũng mãnh, e là Đô Đốc không thể chiếm.","Thắng thua không thể dự đoán. Tào Tháo trước khi về, ra lệnh Tào Nhân thủ Nam Quận, chắc chắn có mưu kế, cộng thêm Tào Nhân dũng mãnh, e là Đô Đốc không thể chiếm.","Chu Du","",},
        [4971] = {9601,"Chu Du","Nếu ta không chiếm được, thì mặc cho công chiếm.","Nếu ta không chiếm được, thì mặc cho công chiếm.","Lưu Bị","",},
        [4972] = {9601,"Lưu Bị","Tử Kính, Khổng Minh làm chứng tại đây, Đô Đốc đừng nuốt lời.","Tử Kính, Khổng Minh làm chứng tại đây, Đô Đốc đừng nuốt lời.","Chu Du","",},
        [4973] = {9601,"Chu Du","Đại trượng phu đã nói ra thì sẽ không hối hận!","Đại trượng phu đã nói ra thì sẽ không hối hận!","Lưu Bị","",},
        [4974] = {9601,"Lưu Bị","Đô Đốc nói vậy, là công luận. Để Đông Ngô chiếm trước, nếu không được, chúa công sẽ chiếm, có gì không được!","Đô Đốc nói vậy, là công luận. Để Đông Ngô chiếm trước, nếu không được, chúa công sẽ chiếm, có gì không được!","Chu Du","",},
        [4975] = {9602,"Lưu Bị","Tiên sinh dạy Bị trả lời như vậy, tuy đã nói theo, nhưng suy đi nghĩ lại vẫn chưa hiểu. Nay ta một thân một mình, không nơi dừng chân, muốn được Nam Quận làm chốn dung thân.","Tiên sinh dạy Bị trả lời như vậy, tuy đã nói theo, nhưng suy đi nghĩ lại vẫn chưa hiểu. Nay ta một thân một mình, không nơi dừng chân, muốn được Nam Quận làm chốn dung thân.","Gia Cát Lượng","",},
        [4976] = {9602,"Lưu Bị","Nếu dạy Chu Du chiếm, thành trì đã thuộc về Đông Ngô, thì làm sao ở?","Nếu dạy Chu Du chiếm, thành trì đã thuộc về Đông Ngô, thì làm sao ở?","Gia Cát Lượng","",},
        [4977] = {9602,"Gia Cát Lượng","Trước đó Lượng khuyên chúa công chiếm Kinh Châu, chúa công không nghe, nay lại muốn chiếm?","Trước đó Lượng khuyên chúa công chiếm Kinh Châu, chúa công không nghe, nay lại muốn chiếm?","Lưu Bị","",},
        [4978] = {9602,"Lưu Bị","Trước đó là nơi của Cảnh Thăng, ta không nỡ chiếm, nay đã là của Tào Tháo, chiếm là hợp lý.","Trước đó là nơi của Cảnh Thăng, ta không nỡ chiếm, nay đã là của Tào Tháo, chiếm là hợp lý.","Gia Cát Lượng","",},
        [4979] = {9602,"Gia Cát Lượng","Không cần chúa công lo lắng. Cứ để Chu Du đi tàn sát, sớm muộn chúa công sẽ ngồi vị trí cao trong Nam Quận Thành.","Không cần chúa công lo lắng. Cứ để Chu Du đi tàn sát, sớm muộn chúa công sẽ ngồi vị trí cao trong Nam Quận Thành.","Lưu Bị","",},
        [4980] = {9602,"Lưu Bị","Ngươi có kế gì?","Ngươi có kế gì?","Gia Cát Lượng","",},
        [4981] = {9602,"Gia Cát Lượng","Hãy ghé tai nghe.","Hãy ghé tai nghe.","Lưu Bị","",},
        [4982] = {9603,"Lỗ Túc","Đô Đốc làm thế nào để Huyền Đức chiếm Nam Quận?","Đô Đốc làm thế nào để Huyền Đức chiếm Nam Quận?","Chu Du","",},
        [4983] = {9603,"Chu Du","Ta búng tay là có được Nam Quận, uổng công làm ơn.","Ta búng tay là có được Nam Quận, uổng công làm ơn.","Lỗ Túc","",},
        [4984] = {9603,"Chu Du","Ai dám chiếm Nam Quận trước?","Ai dám chiếm Nam Quận trước?","Lỗ Túc","",},
        [4985] = {9603,"Tưởng Khâm","Ta!","Ta!","Chu Du","",},
        [4986] = {9603,"Chu Du","Ngươi là Tiên Phong, Từ Thịnh, Đinh Phụng là Phó Tướng, dẫn 5000 lính ngựa tinh nhuệ, qua sông trước. Ta dẫn binh theo tiếp ứng sau.","Ngươi là Tiên Phong, Từ Thịnh, Đinh Phụng là Phó Tướng, dẫn 5000 lính ngựa tinh nhuệ, qua sông trước. Ta dẫn binh theo tiếp ứng sau.","Tưởng Khâm","",},
        [4987] = {9604,"Ngưu Kim","Binh Ngô đã vượt Hán Giang.","Binh Ngô đã vượt Hán Giang.","Tào Nhân","",},
        [4988] = {9604,"Tào Nhân","Giữ vững không giao chiến.","Giữ vững không giao chiến.","Ngưu Kim","",},
        [4989] = {9604,"Ngưu Kim","Binh đến thành mà không chiến, là sợ hãi. Huống chi Ngô binh vừa bại, nên gầy dựng lại nhuệ khí. Ta nguyện mượn năm trăm tinh binh, quyết một trận tử chiến.","Binh đến thành mà không chiến, là sợ hãi. Huống chi Ngô binh vừa bại, nên gầy dựng lại nhuệ khí. Ta nguyện mượn năm trăm tinh binh, quyết một trận tử chiến.","Tào Nhân","",},
        [4990] = {9604,"Tào Nhân","Được, Ngưu Kim ngươi dẫn năm trăm quân xuất chiến.","Được, Ngưu Kim ngươi dẫn năm trăm quân xuất chiến.","Ngưu Kim","",},
        [4991] = {9605,"Chu Du","Ta dẫn binh, phải đích thân quyết chiến Tào Nhân.","Ta dẫn binh, phải đích thân quyết chiến Tào Nhân.","Cam Ninh","",},
        [4992] = {9605,"Cam Ninh","Đô Đốc đừng vội. Nay Tào Nhân lệnh Tào Hồng trấn thủ Di Lăng, tạo thế bao vây, ta nguyện dẫn ba ngàn tinh binh chiếm Di Lăng, sau đó Đô Đốc có thể chiếm Nam Quận.","Đô Đốc đừng vội. Nay Tào Nhân lệnh Tào Hồng trấn thủ Di Lăng, tạo thế bao vây, ta nguyện dẫn ba ngàn tinh binh chiếm Di Lăng, sau đó Đô Đốc có thể chiếm Nam Quận.","Chu Du","",},
        [4993] = {9605,"Chu Du","Sau đó, ngươi dẫn ba ngàn binh tấn công Di Lăng.","Sau đó, ngươi dẫn ba ngàn binh tấn công Di Lăng.","Cam Ninh","",},
        [4994] = {9606,"Tào Nhân","Mật thám báo tin, Đông Ngô Cam Ninh dẫn ba ngàn tinh binh tấn công Di Lăng.","Mật thám báo tin, Đông Ngô Cam Ninh dẫn ba ngàn tinh binh tấn công Di Lăng.","Ngưu Kim","",},
