        [5024] = {9704,"Tào Hồng","Nay mất Di Lăng, tình thế nguy cấp, sao không tháo di kế của thừa tướng để giải nguy?","Nay mất Di Lăng, tình thế nguy cấp, sao không tháo di kế của thừa tướng để giải nguy?","Tào Nhân","",},
