        [5027] = {9704,"Tào Nhân","Thừa tướng lệnh năm canh nấu cơm, bình minh, quân mã lớn nhỏ, đều rời thành, trong thành cắm tinh kỳ, phô trương thanh thế.","Thừa tướng lệnh năm canh nấu cơm, bình minh, quân mã lớn nhỏ, đều rời thành, trong thành cắm tinh kỳ, phô trương thanh thế.","Tào Hồng","",},
        [5028] = {9704,"Tào Hồng","Kế hay kế hay.","Kế hay kế hay.","Tào Nhân","",},
