        [5030] = {9705,"Tào Nhân","Ngô Binh giành vào thành trống trước đều rơi vào bẫy, hai bên bắn cung nỏ, thế như vũ bão, tử thương vô số.","Ngô Binh giành vào thành trống trước đều rơi vào bẫy, hai bên bắn cung nỏ, thế như vũ bão, tử thương vô số.","","",},
        [5031] = {9705,"Tào Nhân","Chu Du vội cưỡi ngựa về, bị một cung tên bắn trúng, lật người ngã ngựa.","Chu Du vội cưỡi ngựa về, bị một cung tên bắn trúng, lật người ngã ngựa.","","",},
