        [5034] = {9706,"Chu Du","Sao lại bắt nạt ta! Ta biết Tào binh thường đến trước trại chửi bới. Trình Đức Mưu cùng nắm binh quyền, sao lại ngồi nhìn?","Sao lại bắt nạt ta! Ta biết Tào binh thường đến trước trại chửi bới. Trình Đức Mưu cùng nắm binh quyền, sao lại ngồi nhìn?","Cam Ninh","",},
        [5035] = {9706,"Trình Phổ","Ta thấy Công Cẩn mắc bệnh, y giả nên cẩn trọng trong lời nói, nên Tào binh khiêu chiến, không dám thông báo.","Ta thấy Công Cẩn mắc bệnh, y giả nên cẩn trọng trong lời nói, nên Tào binh khiêu chiến, không dám thông báo.","Chu Du","",},
        [5036] = {9706,"Chu Du","Ngài không xuất chiến, chủ ý thế nào?","Ngài không xuất chiến, chủ ý thế nào?","Trình Phổ","",},
        [5037] = {9706,"Trình Phổ","Chúng tướng đều muốn thu binh tạm về Giang Đông. Chờ vết thương của ngài bình phục, sẽ an bài sau.","Chúng tướng đều muốn thu binh tạm về Giang Đông. Chờ vết thương của ngài bình phục, sẽ an bài sau.","Chu Du","",},
        [5038] = {9706,"Chu Du","Đại trượng phu ăn quân lộc, hy sinh nơi chiến trường, da ngựa bọc thây là điều đương nhiên! Sao có thể vì mình ta mà chậm trễ quốc gia đại sự?","Đại trượng phu ăn quân lộc, hy sinh nơi chiến trường, da ngựa bọc thây là điều đương nhiên! Sao có thể vì mình ta mà chậm trễ quốc gia đại sự?","Trình Phổ","",},
