        [5058] = {9801,"Chu Du","Cam Ninh dẫn hàng ngàn quân mã, chiếm Kinh Châu.","Cam Ninh dẫn hàng ngàn quân mã, chiếm Kinh Châu.","Trình Phổ","",},
        [5059] = {9801,"Chu Du","Lăng Thống dẫn hàng ngàn quân mã, chiếm Tương Dương, sau đó chiếm Nam Quận cũng chưa muộn.","Lăng Thống dẫn hàng ngàn quân mã, chiếm Tương Dương, sau đó chiếm Nam Quận cũng chưa muộn.","Trình Phổ","",},
        [5060] = {9802,"Trình Phổ","Báo! Gia Cát Lượng chiếm được Nam Quận, dùng binh phù, đêm tối điều quân mã thủ thành Kinh Châu đến cứu.","Gia Cát Lượng đoạt được Nam Quận, dùng Binh Phù điều động binh mã thủ thành Kinh Châu đến cứu viện.","Chu Du","",},
