        [5062] = {9802,"Chu Du","Gì!","Gì!","Trình Phổ","",},
        [5063] = {9803,"Trình Phổ","Báo! Hạ Hầu Đôn ở Tương Dương, bị Gia Cát Lượng sai người đưa binh phù, giả vờ là Tào Nhân cầu cứu, dụ Đôn dẫn quân ra ngoài, rồi dạy Vân Trường chiếm Tương Dương.","Hạ Hầu Đôn ở Tương Dương, Gia Cát Lượng cho người nói dối rằng Tào Nhân cầu cứu, dụ y ra khỏi thành, rồi sai Vân Trường đánh úp Tương Dương.","Chu Du","",},
        [5064] = {9803,"Trình Phổ","Hai thành trì không cần tốn sức đã thuộc về Lưu Huyền Đức.","Hai thành trì không cần tốn sức đã thuộc về Lưu Huyền Đức.","Chu Du","",},
        [5065] = {9803,"Chu Du","Gia Cát Lượng sao có được binh phù?","Gia Cát Lượng sao có được binh phù?","Trình Phổ","",},
        [5066] = {9803,"Trình Phổ","Hắn bắt Trần Kiểu, binh phù đương nhiên thuộc về hắn.","Hắn bắt Trần Kiểu, binh phù đương nhiên thuộc về hắn.","Chu Du","",},
