        [5068] = {9803,"Trình Phổ","Người đâu, mau đỡ Đô Đốc về doanh.","Người đâu, mau đỡ Đô Đốc về doanh.","Chu Du","",},
        [5069] = {9804,"Chu Du","Nếu không diệt Gia Cát thôn phu, sao dập tắt được nộ khí trong lòng ta!","Nếu không diệt Gia Cát thôn phu, sao dập tắt được nộ khí trong lòng ta!","Lỗ Túc","",},
        [5070] = {9804,"Chu Du","Ta muốn khởi binh quyết chiến cùng Lưu Bị, Gia Cát Lượng, đoạt lại thành trì. Tử Kính hãy giúp ta.","Ta muốn khởi binh quyết chiến cùng Lưu Bị, Gia Cát Lượng, đoạt lại thành trì. Tử Kính hãy giúp ta.","Lỗ Túc","",},
        [5071] = {9804,"Lỗ Túc","Không được. Nay đang giằng co với Tào Tháo, chưa phân thắng bại, Chúa công nay công Hợp Phì không được. Nội bộ tranh giành nhau, Tào binh sẽ thừa cơ tiến đến, tình thế sẽ rất nguy.","Không được. Nay đang giằng co với Tào Tháo, chưa phân thắng bại, Chúa công nay công Hợp Phì không được. Nội bộ tranh giành nhau, Tào binh sẽ thừa cơ tiến đến, tình thế sẽ rất nguy.","Chu Du","",},
        [5072] = {9804,"Lỗ Túc","Lưu Huyền Đức từng có quan hệ tốt với Tào Tháo, nếu dồn ép quá, hiến thành trì, cùng công đánh Đông Ngô, thế nào?","Lưu Huyền Đức từng có quan hệ tốt với Tào Tháo, nếu dồn ép quá, hiến thành trì, cùng công đánh Đông Ngô, thế nào?","Chu Du","",},
        [5073] = {9804,"Chu Du","Chúng ta dùng kế sách, tổn binh mã, phí lương tiền, sẵn hết cho hắn, chẳng phải đáng ghét sao!","Chúng ta dùng kế sách, tổn binh mã, phí lương tiền, sẵn hết cho hắn, chẳng phải đáng ghét sao!","Lỗ Túc","",},
        [5074] = {9804,"Lỗ Túc","Công Cẩn hãy kiên nhẫn. Dung mỗ gặp Huyền Đức, nói lí với hắn. Nếu nói không được, lúc đó động binh cũng chưa muộn.","Công Cẩn hãy kiên nhẫn. Dung mỗ gặp Huyền Đức, nói lí với hắn. Nếu nói không được, lúc đó động binh cũng chưa muộn.","Chu Du","",},
        [5075] = {9901,"Lỗ Túc","Chủ ta Ngô Hầu, và Đô Đốc Công Cẩn, Giáo mỗ nhiều lần tỏ ý với hoàng thúc, dẫn trăm vạn người lấy danh xuống Giang Nam, thực chất là thực hiện mưu đồ với hoàng thúc.","Chủ ta Ngô Hầu, và Đô Đốc Công Cẩn, Giáo mỗ nhiều lần tỏ ý với hoàng thúc, dẫn trăm vạn người lấy danh xuống Giang Nam, thực chất là thực hiện mưu đồ với hoàng thúc.","Gia Cát Lượng","",},
        [5076] = {9901,"Lỗ Túc","May có Đông Ngô diệt lùi Tào binh, cứu hoàng thúc. Tất cả Kinh Châu Cửu Quận, đều quy Đông Ngô.","May có Đông Ngô diệt lùi Tào binh, cứu hoàng thúc. Tất cả Kinh Châu Cửu Quận, đều quy Đông Ngô.","Gia Cát Lượng","",},
        [5077] = {9901,"Lỗ Túc","Nay hoàng thúc dùng ngụy kế, đoạt chiếm Kinh Tương, khiến Giang Đông tốn lương tiền quân mã, còn hoàng thúc thu lợi, e là không thuận với lý.","Nay hoàng thúc dùng ngụy kế, đoạt chiếm Kinh Tương, khiến Giang Đông tốn lương tiền quân mã, còn hoàng thúc thu lợi, e là không thuận với lý.","Gia Cát Lượng","",},
        [5078] = {9901,"Gia Cát Lượng","Tử Kính là người cao minh, sao lại nói thế? Thường nói là: Vật ắt quy chủ. Kinh Tương Cửu Quận, không phải đất của Đông Ngọ, là cơ nghiệp của Lưu Cảnh Thăng.","Tử Kính là người cao minh, sao lại nói thế? Thường nói là: Vật ắt quy chủ. Kinh Tương Cửu Quận, không phải đất của Đông Ngọ, là cơ nghiệp của Lưu Cảnh Thăng.","Lỗ Túc","",},
        [5079] = {9901,"Gia Cát Lượng","Chủ nhân ta là đệ đệ của Cảnh Thăng. Cảnh Thăng tuy qua đời, nhưng nhi tử người vẫn còn, thúc thúc phụ trợ cháu chiếm Kinh Châu, có gì không được?","Chủ nhân ta là đệ đệ của Cảnh Thăng. Cảnh Thăng tuy qua đời, nhưng nhi tử người vẫn còn, thúc thúc phụ trợ cháu chiếm Kinh Châu, có gì không được?","Lỗ Túc","",},
        [5080] = {9901,"Lỗ Túc","Nếu là công tử Lưu Kỳ chiếm cứ, có thể hiểu được, nay công tử ở Giang Hạ, không ở nơi này!","Nếu là công tử Lưu Kỳ chiếm cứ, có thể hiểu được, nay công tử ở Giang Hạ, không ở nơi này!","Gia Cát Lượng","",},
        [5081] = {9901,"Gia Cát Lượng","Tử Kính muốn gặp công tử sao?","Tử Kính muốn gặp công tử sao?","Lỗ Túc","",},
        [5082] = {9901,"Lỗ Túc","Được!","Được!","Gia Cát Lượng","",},
        [5083] = {9901,"Gia Cát Lượng","Khổng Minh sẽ dẫn ngươi gặp ngài.","Khổng Minh sẽ dẫn ngươi gặp ngài.","Lỗ Túc","",},
        [5084] = {9902,"Lưu Kỳ","Bệnh hoạn không thể hành lễ, Tử Kính đừng trách. (Lưu Kỳ nằm trên giường.)","Bệnh hoạn không thể hành lễ, Tử Kính đừng trách. (Lưu Kỳ nằm trên giường.)","Lỗ Túc","",},
        [5085] = {9902,"Lỗ Túc","Nếu công tử không còn thì làm sao?","Nếu công tử không còn thì làm sao?","Lưu Kỳ","",},
        [5086] = {9902,"Gia Cát Lượng","Công tử còn một ngày, thủ một ngày, nếu không còn, sẽ bàn bạc lại.","Công tử còn một ngày, thủ một ngày, nếu không còn, sẽ bàn bạc lại.","Lỗ Túc","",},
        [5087] = {9902,"Lỗ Túc","Nếu công tử không ở đây, phải trả thành cho Đông Ngô.","Nếu công tử không ở đây, phải trả thành cho Đông Ngô.","Gia Cát Lượng","",},
        [5088] = {9902,"Gia Cát Lượng","Tử Kính nói phải.","Tử Kính nói phải.","Lỗ Túc","",},
        [5089] = {9903,"Lỗ Túc","Sau khi Lưu Kỳ mất, thành trì sẽ trả cho Đông Ngô ta.","Sau khi Lưu Kỳ mất, thành trì sẽ trả cho Đông Ngô ta.","Chu Du","",},
        [5090] = {9903,"Chu Du","Lưu Kỳ còn trẻ, sao có thể bỏ mạng? Khi nào mới có lại Kinh Châu?","Lưu Kỳ còn trẻ, sao có thể bỏ mạng? Khi nào mới có lại Kinh Châu?","Lỗ Túc","",},
        [5091] = {9903,"Lỗ Túc","Đô Đốc yên tâm. Giao cho Lỗ Túc, chắc chắn lấy Kinh Tương trả lại Đông Ngô.","Đô Đốc yên tâm. Giao cho Lỗ Túc, chắc chắn lấy Kinh Tương trả lại Đông Ngô.","Chu Du","",},
        [5092] = {9903,"Chu Du","Tử Kính có cao kiến gì?","Tử Kính có cao kiến gì?","Lỗ Túc","",},
        [5093] = {9903,"Lỗ Túc","Ta thấy Lưu Kỳ tửu sắc quá độ, bệnh nặng nguy kịch, nay sắc mặt xanh xao, khó thở, không quá nửa năm chắc chắn qua đời.","Ta thấy Lưu Kỳ tửu sắc quá độ, bệnh nặng nguy kịch, nay sắc mặt xanh xao, khó thở, không quá nửa năm chắc chắn qua đời.","Chu Du","",},
        [5094] = {9903,"Lỗ Túc","Lúc đó đi chiếm Kinh Châu, Lưu Bị sẽ không có lí do từ chối.","Lúc đó đi chiếm Kinh Châu, Lưu Bị sẽ không có lí do từ chối.","Chu Du","",},
        [5095] = {9903,"Chu Du","Được.","Được.","Lỗ Túc","",},
        [5096] = {9904,"Y Tịch","Phải biết kế lâu dài của Kinh Châu, sao không hỏi hiền sĩ?","Phải biết kế lâu dài của Kinh Châu, sao không hỏi hiền sĩ?","Lưu Bị","",},
        [5097] = {9904,"Lưu Bị","Hiền sĩ ở đâu?","Hiền sĩ ở đâu?","Y Tịch","",},
        [5098] = {9904,"Y Tịch","Kinh Tương Mã Thị, năm huynh đệ đều có tài danh: người nhỏ tên Tắc, tự Ấu Thường, người hiền tài nhất, giữa chân mày có lông trắng, tên Lương, tự Quý Thường.","Kinh Tương Mã Thị, năm huynh đệ đều có tài danh: người nhỏ tên Tắc, tự Ấu Thường, người hiền tài nhất, giữa chân mày có lông trắng, tên Lương, tự Quý Thường.","Lưu Bị","",},
        [5099] = {9904,"Y Tịch","Có câu: Mã Thị ngũ thường, bạch mi tối lương. Sao ngài không nhờ người này hiến kế?","Có câu: Mã Thị ngũ thường, bạch mi tối lương. Sao ngài không nhờ người này hiến kế?","Lưu Bị","",},
        [5100] = {9904,"Lưu Bị","Mau mời người này.","Mau mời người này.","Y Tịch","",},
        [5101] = {9905,"Lưu Bị","Tiên sinh có kế sách giữ Kinh Tương không.","Tiên sinh có kế sách giữ Kinh Tương không.","Mã Lương","",},
        [5102] = {9905,"Mã Lương","Kinh Tương bị địch bao vây, e là không thể giữ lâu, nhưng lệnh công tử Lưu Kỳ ở đây dưỡng bệnh, chiêu dụ cựu nhân trấn thủ, Biểu Tấu công tử làm Kinh Châu Thứ Sử để dân an lòng.","Kinh Tương bị địch bao vây, e là không thể giữ lâu, nhưng lệnh công tử Lưu Kỳ ở đây dưỡng bệnh, chiêu dụ cựu nhân trấn thủ, Biểu Tấu công tử làm Kinh Châu Thứ Sử để dân an lòng.","Lưu Bị","",},
        [5103] = {9905,"Mã Lương","Sau đó nam chinh Vũ Lân, Phượng Tường, Quế Dương, Linh Lăng, tích thu lương tiền làm vốn. Đây là kế lâu dài.","Sau đó nam chinh Vũ Lân, Phượng Tường, Quế Dương, Linh Lăng, tích thu lương tiền làm vốn. Đây là kế lâu dài.","Lưu Bị","",},
        [5104] = {9905,"Lưu Bị","Bốn quận nên chiếm quận nào trước?","Bốn quận nên chiếm quận nào trước?","Mã Lương","",},
        [5105] = {9905,"Mã Lương","Phía tây Tương Giang, Linh Lăng gần nhất, nên chiếm trước, kế đến là Vũ Lân. Sau đó phía đông Tương Giang chiếm Quế Dương, Phượng Tường cuối cùng.","Phía tây Tương Giang, Linh Lăng gần nhất, nên chiếm trước, kế đến là Vũ Lân. Sau đó phía đông Tương Giang chiếm Quế Dương, Phượng Tường cuối cùng.","Lưu Bị","",},
        [5106] = {9905,"Lưu Bị","Được! Tiễn Lưu Kỳ về Tương Dương, thay Vân Trường về Kinh Châu.","Được! Tiễn Lưu Kỳ về Tương Dương, thay Vân Trường về Kinh Châu.","Mã Lương","",},
        [5107] = {9905,"Lưu Bị","Điều binh chiếm Linh Lăng, sai Trương Phi làm Tiên Phong, Triệu Vân hợp hậu, Khổng Minh và ta là trung quân, nhân mã một vạn năm ngàn, Vân Trường thủ Kinh Châu, My Trúc, Lưu Phong thủ Giang Lăng.","Điều binh chiếm Linh Lăng, sai Trương Phi làm Tiên Phong, Triệu Vân hợp hậu, Khổng Minh và ta là trung quân, nhân mã một vạn năm ngàn, Vân Trường thủ Kinh Châu, My Trúc, Lưu Phong thủ Giang Lăng.","Mã Lương","",},
        [5108] = {10001,"Gia Cát Lượng","Ta là Hoa Dương Gia Cát Khổng Minh. Tào Tháo dẫn trăm vạn người, trúng kế của ta bị diệt tan tác.","Ta là Hoa Dương Gia Cát Khổng Minh. Tào Tháo dẫn trăm vạn người, trúng kế của ta bị diệt tan tác.","Hình Đạo Vinh","",},
        [5109] = {10001,"Gia Cát Lượng","Các ngươi sao có thể đối địch ta? Ta đến chiêu hàng các ngươi, còn không sớm đầu hàng?","Các ngươi sao có thể đối địch ta? Ta đến chiêu hàng các ngươi, còn không sớm đầu hàng?","Hình Đạo Vinh","",},
        [5110] = {10001,"Hình Đạo Vinh","Xích Bích ác chiến, là mưu kế của Chu Lang, liên quan gì đến ngươi, dám nói lời ngông cuồng!","Xích Bích ác chiến, là mưu kế của Chu Lang, liên quan gì đến ngươi, dám nói lời ngông cuồng!","Gia Cát Lượng","",},
        [5111] = {10001,"Hình Đạo Vinh","Hỏi binh khí trong tay ta đi!","Hỏi binh khí trong tay ta đi!","Gia Cát Lượng","",},
        [5112] = {10002,"Triệu Vân","Nhận ra Triệu Tử Long không!","Nhận ra Triệu Tử Long không!","Hình Đạo Vinh","",},
        [5113] = {10002,"Triệu Vân","Chạy đi đâu!","Chạy đi đâu!","Hình Đạo Vinh","",},
        [5114] = {10002,"Hình Đạo Vinh","Tử Long tha mạng!","Tử Long tha mạng!","Triệu Vân","",},
        [5115] = {10003,"Gia Cát Lượng","Nếu ngươi cùng ta bắt được Lưu Hiền, sẽ cho ngươi đầu hàng.","Nếu ngươi cùng ta bắt được Lưu Hiền, sẽ cho ngươi đầu hàng.","Hình Đạo Vinh","",},
        [5116] = {10003,"Hình Đạo Vinh","Ta nguyện đi.","Ta nguyện đi.","Gia Cát Lượng","",},
        [5117] = {10003,"Gia Cát Lượng","Ngươi dùng cách gì bắt hắn?","Ngươi dùng cách gì bắt hắn?","Hình Đạo Vinh","",},
        [5118] = {10003,"Hình Đạo Vinh","Quân sư nếu đồng ý thả ta về, ta tự có cách. Tối nay quân sư điều quân cướp trại, ta làm nội ứng, bắt sống Lưu Hiền giao cho quân sư. Lưu Hiền bị bắt, Lưu Độ sẽ tự đầu hàng.","Quân sư nếu đồng ý thả ta về, ta tự có cách. Tối nay quân sư điều quân cướp trại, ta làm nội ứng, bắt sống Lưu Hiền giao cho quân sư. Lưu Hiền bị bắt, Lưu Độ sẽ tự đầu hàng.","Gia Cát Lượng","",},
        [5119] = {10003,"Lưu Bị","Sao ta tin ngươi được?","Sao ta tin ngươi được?","Hình Đạo Vinh","",},
        [5120] = {10003,"Hình Đạo Vinh","Ta tuyệt đối không nói dối!","Ta tuyệt đối không nói dối!","Lưu Bị","",},
        [5121] = {10003,"Gia Cát Lượng","Ta tin Hình Tướng Quân ngươi không nói dối, ngươi về đi.","Ta tin Hình Tướng Quân ngươi không nói dối, ngươi về đi.","Hình Đạo Vinh","",},
        [5122] = {10003,"Hình Đạo Vinh","Đa tạ Khổng Minh.","Đa tạ Khổng Minh.","Gia Cát Lượng","",},
        [5123] = {10004,"Hình Đạo Vinh","Lưu Bị thả ta về làm nội ứng, phải bắt sống ngươi, để Lưu Độ tự hàng.","Lưu Bị thả ta về làm nội ứng, phải bắt sống ngươi, để Lưu Độ tự hàng.","Lưu Hiền","",},
        [5124] = {10004,"Lưu Hiền","Vậy phải làm sao?","Vậy phải làm sao?","Hình Đạo Vinh","",},
        [5125] = {10004,"Hình Đạo Vinh","Tương kế tựu kế. Tối nay cho quân mai phục ngoài trại, trong trại giả vờ cắm cờ, chờ Khổng Minh đến cướp trại thì bắt hắn.","Tương kế tựu kế. Tối nay cho quân mai phục ngoài trại, trong trại giả vờ cắm cờ, chờ Khổng Minh đến cướp trại thì bắt hắn.","Lưu Hiền","",},
        [5126] = {10004,"Lưu Hiền","Được.","Được.","Hình Đạo Vinh","",},
        [5127] = {10005,"Lưu Hiền","Một nhóm quân đến cửa trại, tay cầm đuốc định đốt trại.","Một nhóm quân đến cửa trại, tay cầm đuốc định đốt trại.","Hình Đạo Vinh","",},
        [5128] = {10005,"Hình Đạo Vinh","Mau tóm lấy Khổng Minh.","Mau tóm lấy Khổng Minh.","Lưu Hiền","",},
        [5129] = {10005,"Lưu Hiền","Được!","Được!","Hình Đạo Vinh","",},
        [5130] = {10006,"Hình Đạo Vinh","Đuổi kịp 10 dặm, quân không thấy đâu.","Đuổi kịp 10 dặm, quân không thấy đâu.","Lưu Hiền","",},
        [5131] = {10006,"Hình Đạo Vinh","Trúng kế rồi, mau chuồn thôi!","Trúng kế rồi, mau chuồn thôi!","Lưu Hiền","",},
        [5132] = {10006,"Lưu Hiền","Không thể vào trại, có thể trại đã bị chiếm","Không thể vào trại, có thể trại đã bị chiếm","Hình Đạo Vinh","",},
        [5133] = {10006,"Lưu Hiền","Đến cướp Trại Khổng Minh là được.","Đến cướp Trại Khổng Minh là được.","Hình Đạo Vinh","",},
        [5134] = {10007,"Triệu Vân","Lưu Hiền trốn đâu cho thoát!","Lưu Hiền trốn đâu cho thoát!","Lưu Hiền","",},
        [5135] = {10007,"Lưu Hiền","Ta đến đầu hàng!","Ta đến đầu hàng!","Triệu Vân","",},
        [5136] = {10007,"Lưu Hiền","Hình Đạo Vinh xui ta như thế chứ không phải chủ ý của ta.","Hình Đạo Vinh xui ta như thế chứ không phải chủ ý của ta.","Triệu Vân","",},
        [5137] = {10008,"Triệu Vân","Lưu Hiền bị bắt, nếu Lưu Độ Nhữ không hàng thì chiếm thành diệt môn","Lưu Hiền bị bắt, nếu Lưu Độ Nhữ không hàng thì chiếm thành diệt môn","Lưu Độ","",},
