        [435] = {1006,15,2,"关银屏",217,0,"董卓死后，他的部下李傕郭汜等人已经引了飞熊军逃往凉州去了。","董卓死后，他的部下李傕郭汜等人已经引了飞熊军逃往凉州去了。",1,"story_gyp_55","story_gyp_55","主角",1,},
