        [440] = {1102,1,2,"李傕",431,0,"该死我又失败了！小子，下次见面我一定杀了你。（李傕疯狂逃跑）","该死我又失败了！小子，下次见面我一定杀了你。（李傕疯狂逃跑）",1,"story_lj_3","story_lj_3","张星彩",216,},
