        [499] = {1203,3,2,"公孙越",134,0,"公孙瓒是我哥哥！哥哥派我来冀州，找袁绍讨要冀州地盘。","公孙瓒是我哥哥！哥哥派我来冀州，找袁绍讨要冀州地盘。",1,"story_gsy_2","story_gsy_2","主角",1,},
