        [746] = {2018203,1,2,"何太后",426,0,"哥哥，我已经做了皇后了！母凭子贵，谁让我给皇帝生了个皇子呢！","哥哥，我已经做了皇后了！母凭子贵，谁让我给皇帝生了个皇子呢！",1,"story_hth_7","story_hth_7","",999,},
        [747] = {2018203,2,1,"何进",427,0,"太好了，我们何家终于出人头地了！","太好了，我们何家终于出人头地了！",1,"story_hj_7","story_hj_7","何太后",426,},
        [748] = {2018203,3,2,"何太后",426,0,"哥哥，多亏你在黄巾之乱中立了大功，抓了马元义！你这个大将军真厉害！","哥哥，多亏你在黄巾之乱中立了大功，抓了马元义！你这个大将军真厉害！",1,"story_hth_8","story_hth_8","",999,},
        [749] = {2018203,4,1,"何进",427,0,"哈哈哈，那是！我当了大将军，才能保护你！","哈哈哈，那是！我当了大将军，才能保护你！",1,"story_hj_8","story_hj_8","",999,},
        [750] = {2018203,5,2,"何太后",426,0,"哥哥，皇帝想要废长立幼，他想立王美人生的孩子刘协为皇帝。呜呜呜，你可要为我做主啊。","哥哥，皇帝想要废长立幼，他想立王美人生的孩子刘协为皇帝。呜呜呜，你可要为我做主啊。",1,"story_hth_9","story_hth_9","",999,},
        [751] = {2018203,6,1,"何进",427,0,"妹妹放心，皇帝想要废长立幼，绝不可能，哥哥一定不会让这种事情发生！","妹妹放心，皇帝想要废长立幼，绝不可能，哥哥一定不会让这种事情发生！",1,"story_hj_9","story_hj_9","",999,},
        [752] = {2018204,1,1,"何进",427,0,"妹妹，曹操袁绍等人都劝我诛杀十常侍，我一定得设法杀了张让！","妹妹，曹操袁绍等人都劝我诛杀十常侍，我一定得设法杀了张让！",1,"story_hj_10","story_hj_10","何太后",426,},
        [753] = {2018204,2,2,"何太后",426,0,"哥哥，不可以啊！张让曾经救过我的性命，若不是他，皇帝早就废了我！","哥哥，不可以啊！张让曾经救过我的性命，若不是他，皇帝早就废了我！",1,"story_hth_10","story_hth_10","",999,},
        [754] = {2018204,3,1,"何进",427,0,"妹妹，我们何家想要站稳这朝堂，就必须除掉张让，否则，刘辩还小，迟早会成为心腹大患啊。","妹妹，我们何家想要站稳这朝堂，就必须除掉张让，否则，刘辩还小，迟早会成为心腹大患啊。",1,"story_hj_11","story_hj_11","",999,},
        [755] = {2018204,4,1,"何进",427,0,"妹妹，我们何家想要站稳这朝堂，就必须除掉张让，否则，刘辩还小，迟早会成为心腹大患啊。","妹妹，我们何家想要站稳这朝堂，就必须除掉张让，否则，刘辩还小，迟早会成为心腹大患啊。",1,"story_hj_12","story_hj_12","",999,},
        [756] = {2018204,5,2,"何太后",426,0,"哥哥，不行，无论如何，你也不能杀掉张让。","哥哥，不行，无论如何，你也不能杀掉张让。",1,"story_hth_11","story_hth_11","",999,},
        [757] = {2018204,6,1,"何进",427,0,"好吧，我另外再想办法！","好吧，我另外再想办法！",1,"story_hj_13","story_hj_13","",999,},
        [758] = {2018205,1,2,"张让",419,0,"如果不是其他小太监偷偷来报信，我竟然不知道，何进竟想诛杀我。","如果不是其他小太监偷偷来报信，我竟然不知道，何进竟想诛杀我。",1,"story_zr2_11","story_zr2_11","",0,},
        [759] = {2018205,2,2,"张让",419,0,"既然如此，那就别怪我不客气了。来人，假传太后懿旨，将何进骗进宫来诛杀！","既然如此，那就别怪我不客气了。来人，假传太后懿旨，将何进骗进宫来诛杀！",1,"story_zr2_12","story_zr2_12","",0,},
        [760] = {2018206,1,2,"袁绍",411,0,"太后此诏，必是十常侍之谋，切不可去，去必有祸。","太后此诏，必是十常侍之谋，切不可去，去必有祸。",1,"story_ys1_13","story_ys1_13","何进",427,},
        [761] = {2018206,2,1,"何进",427,0,"太后是我亲妹妹，能有什么祸事？","太后是我亲妹妹，能有什么祸事？",1,"story_hj_14","story_hj_14","",999,},
        [762] = {2018206,3,2,"袁绍",411,0,"大将军如果非要进宫，那我与袁绍带人保护，以防止不测。","大将军如果非要进宫，那我与袁绍带人保护，以防止不测。",1,"story_ys1_14","story_ys1_14","",999,},
        [763] = {2018206,4,1,"何进",427,0,"我妹妹是当朝太后，我是当朝大将军，谁敢杀我！更何况，倘若真是十常侍的阴谋，我更要进宫去保护我妹妹！","我妹妹是当朝太后，我是当朝大将军，谁敢杀我！更何况，倘若真是十常侍的阴谋，我更要进宫去保护我妹妹！",1,"story_hj_15","story_hj_15","",999,},
        [764] = {2018206,5,1,"何进",427,0,"我妹妹是当朝太后，我是当朝大将军，谁敢杀我！更何况，倘若真是十常侍的阴谋，我更要进宫去保护我妹妹！","我妹妹是当朝太后，我是当朝大将军，谁敢杀我！更何况，倘若真是十常侍的阴谋，我更要进宫去保护我妹妹！",1,"story_hj_16","story_hj_16","",999,},
        [765] = {2018301,1,1,"华雄",406,0,"李傕，臭小子，人呢！走，喝酒去！","李傕，臭小子，人呢！走，喝酒去！",1,"story_hx1_2","story_hx1_2","李傕",431,},
        [766] = {2018301,2,2,"李傕",431,0,"喝酒好说，不过这酒钱……","喝酒好说，不过这酒钱……",1,"story_lj_7","story_lj_7","",999,},
        [767] = {2018301,3,1,"华雄",406,0,"好说好说！跟哥哥提钱，见外了啊。","好说好说！跟哥哥提钱，见外了啊。",1,"story_hx1_3","story_hx1_3","",999,},
        [768] = {2018302,1,2,"董卓",405,0,"那十八路诸侯已经到了虎牢关外，谁可出战？","那十八路诸侯已经到了虎牢关外，谁可出战？",1,"story_dz1_12","story_dz1_12","华雄",406,},
        [769] = {2018302,2,1,"吕布",403,0,"父亲勿忧，关外诸侯，布视之如草芥，愿提虎狼之师，尽斩其首，悬于都门。","父亲勿忧，关外诸侯，布视之如草芥，愿提虎狼之师，尽斩其首，悬于都门。",1,"story_lb1_36","story_lb1_36","",999,},
        [770] = {2018302,3,1,"华雄",406,0,"割鸡焉用牛刀，不劳温侯亲往。吾斩众诸侯首级，如探囊取物耳！","割鸡焉用牛刀，不劳温侯亲往。吾斩众诸侯首级，如探囊取物耳！",1,"story_hx1_4","story_hx1_4","",999,},
        [771] = {2018303,1,2,"董卓",405,0,"华雄，你功劳不小啊，先斩鲍忠，又斩祖茂，大败孙坚，厉害厉害。来人，封华雄为都督！","华雄，你功劳不小啊，先斩鲍忠，又斩祖茂，大败孙坚，厉害厉害。来人，封华雄为都督！",1,"story_dz1_13","story_dz1_13","",999,},
        [772] = {2018303,2,1,"华雄",406,0,"谢太师！","谢太师！",1,"story_hx1_5","story_hx1_5","",999,},
        [773] = {2018304,1,1,"华雄",406,0,"（用长竿挑着孙坚的红头巾）一群懦夫，谁敢来战？","（用长竿挑着孙坚的红头巾）一群懦夫，谁敢来战？",1,"story_hx1_6","story_hx1_6","袁绍",411,},
        [774] = {2018304,2,2,"袁绍",411,0,"可惜我手下大将颜良文丑不在，诸位，那华雄好生嚣张，谁敢应战？","可惜我手下大将颜良文丑不在，诸位，那华雄好生嚣张，谁敢应战？",1,"story_ys1_15","story_ys1_15","",999,},
        [775] = {2018304,3,2,"关羽",205,0,"某愿往斩华雄头，献于账下！酒且温下，某去去便来。","某愿往斩华雄头，献于账下！酒且温下，某去去便来。",1,"story_gy1_8","story_gy1_8","",999,},
