        [818] = {2018501,1,2,"沙摩柯",231,0,"咳咳，因前几日染了风寒，手中无钱财，请小娘子好心，施舍些钱银，让我去找大夫医治把。","咳咳，因前几日染了风寒，手中无钱财，请小娘子好心，施舍些钱银，让我去找大夫医治把。",1,"story_smk_1","story_smk_1","小娘子",130,},
        [819] = {2018501,2,1,"小娘子",130,0,"这次疫情颇重，听闻大贤良师张角的符水，能治病，你不如去求他。","这次疫情颇重，听闻大贤良师张角的符水，能治病，你不如去求他。",1,"story_xnz_1","story_xnz_1","沙摩柯",231,},
