        [822] = {2018501,5,1,"张角",409,0,"我张角得南华老仙传授《太平要术》，自创立太平道，已有弟子三十六万！","我张角得南华老仙传授《太平要术》，自创立太平道，已有弟子三十六万！",1,"story_zj4_9","story_zj4_9","沙摩柯",231,},
