        [831] = {2018502,7,1,"张角",409,0,"今汉运将终，大圣人出。汝等皆宜顺天从正，以乐太平。","今汉运将终，大圣人出。汝等皆宜顺天从正，以乐太平。",1,"story_zj4_15","story_zj4_15","张梁",420,},
        [832] = {2018502,8,1,"张角",409,0,"哈哈！苍天已死，黄天当立，岁在甲子，天下大吉！以我之真气，合天地之造化！","哈哈！苍天已死，黄天当立，岁在甲子，天下大吉！以我之真气，合天地之造化！",1,"story_zj4_16","story_zj4_16","张梁",420,},
